import React, { Component } from "react";
import { URL } from "../../Util/Constant";

class TokenIFrame extends Component {
  componentDidMount() {
    window.addEventListener("message", this.onMessage, false);
    window.parent.postMessage({ type: "ready" }, "*");
  }

  componentWillUnmount() {
    window.removeEventListener("message", this.onMessage);
  }

  onMessage = message => {
    if ( message.origin.includes('redbrick.space') || message.origin.includes('wizlab.net')) {
        if (message.data.type === "requestToken") {
        const token = localStorage.getItem("wizToken");
        window.parent.postMessage({ type: "token", token }, "*");
      }
    }
  };

  render() {
    return <div />;
  }
}

export default TokenIFrame;
