function loginId(loginId) {
  if (!loginId || loginId === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_LOGINID_NULL" };
  }

  if (!/^[0-9a-zA-Z-_]{5,20}$/.test(loginId)) {
    return { result: false, id: "ID_VALIDATE_FAIL_LOGINID_FORMAT" };
  }

  if (loginId.length < 5) {
    return { result: false, id: "ID_VALIDATE_FAIL_LOGINID_LENGTH" };
  }

  return { result: true };
}

function password(password) {
  if (!password || password === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_PW_NULL" };
  }

  if (!passwordLength(password)) {
    return { result: false, id: "ID_VALIDATE_FAIL_PW_LENGTH" };
  }

  if (!passwordComb(password)) {
    return { result: false, id: "ID_VALIDATE_FAIL_PW_COMB" };
  }

  return { result: true };
}

function passwordLength(password) {
  if (!/^[0-9a-zA-Z~`!@#$%\\^&*()-+=]{8,}$/.test(password)) {
    return false;
  }
  return true;
}

function passwordComb(password) {
  const isNumExist = password.search(/[0-9]/g) >= 0;
  const isEngExist = password.search(/[a-z]/gi) >= 0;
  return isNumExist && isEngExist;
}

function passwordRe(password, password_re) {
  if (!password_re || password_re === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_PWRE_NULL" };
  }

  if (password !== password_re) {
    return { result: false, id: "ID_VALIDATE_FAIL_PWRE_DIFF" };
  }

  return { result: true };
}

function email(email) {
  if (!email || email === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_EMAIL_NULL" };
  }

  if (!/^[a-z0-9_+.-]+@([a-z0-9-]+\.)+[a-z0-9]{2,4}$/.test(email)) {
    return { result: false, id: "ID_VALIDATE_FAIL_EMAIL_FORMAT" };
  }

  return { result: true };
}

function phone(phone) {
  if (!phone || phone === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_PHONE_NULL" };
  }

  const regEx = /^[0-9]{3}[0-9]{3,4}[0-9]{4}$/;

  if (!regEx.test(phone)) {
    return { result: false, id: "ID_VALIDATE_FAIL_PHONE_LENGTH" };
  }

  return { result: true };
}

function certcode(certcode) {
  if (!certcode || certcode === "") {
    return { result: false, id: "ID_VALIDATE_FAIL_CERTCODE_NULL" };
  }

  return { result: true };
}

function isEmpty(txt, messageId) {
  if (!txt || txt === "") {
    return { result: false, id: messageId };
  }

  return { result: true };
}

const Validate = {
  loginId,
  password,
  passwordRe,
  passwordLength,
  passwordComb,
  email,
  phone,
  certcode,
  isEmpty
};
export default Validate;
