import React from 'react';
import Layout from '../../Common/Component/Layout';
import { FormattedMessage } from "react-intl";
import CampInfo from "./Component/CampInfo";
import CampProjects from "./Component/CampProjects";
import "./index.scss";

export default function View(props) {
  const {
    currentTab,
    tabs,
    onClickTab
  } = props;

  return (
    <Layout>
      <div className="camp">
        <div className="camp__banner"/>
        <div className="camp__tabs">
          {tabs.map((tab, i)=>{
            return (
              <div
                className={`camp__tab ${currentTab===tab && "camp__tab--active"}`}
                onClick={()=> onClickTab(tab)}
                key={i}
              >
                <FormattedMessage id={`ID_CAMP_TAB_${tab.toUpperCase()}`} />
              </div>
            );
          })}
        </div>
        {currentTab === "info" && <CampInfo />}
        {currentTab === "projects" && <CampProjects mode="projects" />}
        {currentTab === "hof" && <CampProjects mode="hof" />}
      </div>
    </Layout>
  )
}
