import React from "react";
import Layout from "../../Common/Component/Layout";
import "./index.scss";
import { Link } from "react-router-dom";

export default function View(props) {
  const { selectedTab, TAB_TYPE, events, results } = props;
  const { handleSelectTab, intl } = props;

  let content;

  switch (selectedTab) {
    case TAB_TYPE.ING:
      content = (
        <div className="event__items-item">
          {events.map((event, index) => (
            <EventItem event={event} key={index} intl={intl} type="ing" />
          ))}
          {events.length === 0 && (
            <p className="no-event-text">{intl.formatMessage({ id: "ID_EVENT_PLACEHOLDER" })}</p>
          )}
        </div>
      );
      break;
    case TAB_TYPE.ANNOUNCE:
      content = (
        <div className="event__items-item">
          {results.map((result, index) => (
            <EventItem event={result} key={index} intl={intl} type="announce" />
          ))}
        </div>
      );
      break;
    default:
      break;
  }

  return (
    <Layout>
      <div className="event">
        <div className="event__title">
          {intl.formatMessage({ id: "ID_EVENT_TITLE" })}
        </div>
        <div className="event__tabs">
          {[...Object.keys(TAB_TYPE)].map((tab, index) => {
            return (
              <div
                key={index}
                className={`event__tab ${selectedTab === TAB_TYPE[tab] ? "tab--selected" : ""}`}
                onClick={() => handleSelectTab(TAB_TYPE[tab])}
              >
                {TAB_TYPE[tab]}
              </div>
            );
          })}
        </div>
        <section className="event__items">{content}</section>
      </div>
    </Layout>
  );
}

const EventItem = props => {
  const { event } = props;
  return (
    <Link
      className="event-item"
      to={event.path}
      style={{ textDecoration: "none" }}
    >
      <div className="event-item__image">
        <img src={event.coverImg} alt="img" />
      </div>
      {/* TODO : event subtitle 보여주도록 처리 */}
      {/* <div className="event-item__subtitle">
        {type === "ing"
          ? intl.formatMessage({ id: "ID_EVENT_LIVE" })
          : intl.formatMessage({ id: "ID_EVENT_RESULT" })}
      </div> */}
      <div className="event-item__title">
        {event.title}
        {/* TODO: new 표시 */}
        {/* <span className="new-event">New</span> */}
      </div>
      <div className="event-item__date">{`${event.startDate} ~ ${event.expiredDate}`}</div>
    </Link>
  );
};
