import React from 'react';
import Layout from '../../../Common/Component/Layout';
import { FormattedMessage } from "react-intl";
import ChallengeInfo from "./Component/ChallengeInfo";
import ChallengeProject from "./Component/ChallengeProject";
import ChallengeHallOfFame from "./Component/ChallengeHallOfFame";
import "./index.scss";

export default function View(props) {
  const { 
    currentTab, 
    onClickTab, 
    onClickApplyBtn,
    onClickConfirmBtn
  } = props;

  return (
    <Layout className="layout--challenge">
      <div className="challenge challenge--gstar">
        <div className="challenge__info-image-wrapper">
          <div className="challenge__info-image" />
        </div>
        <div className="challenge__buttons">
          <button className="challenge__button--apply">
            <FormattedMessage id="ID_CHALLENGE_BUTTON_APPLY" />
          </button>
          <button className="challenge__button--confirm" onClick={onClickConfirmBtn}>
            <FormattedMessage id="ID_CHALLENGE_BUTTON_CONFIRM" />
          </button>
        </div>

        <div className="challenge__tabs">
          <div 
            className={`challenge__tab ${currentTab === "info" && "tab--active"}`}
            onClick={() => onClickTab("info")}
          >
            <FormattedMessage id="ID_CHALLENGE_TAB_INTRO" />
          </div>
          <div 
            className={`challenge__tab ${currentTab === "project" && "tab--active"}`}
            onClick={() => onClickTab("project")}
          >
            <FormattedMessage id="ID_CHALLENGE_TAB_WORK" />
          </div>
          <div 
            className={`challenge__tab ${currentTab === "hallOfFame" && "tab--active"}`}
            onClick={() => onClickTab("hallOfFame")}
          >
            <FormattedMessage id="ID_CHALLENGE_TAB_HOF" />
          </div>
        </div>

        {currentTab === "info" && <ChallengeInfo />}
        {currentTab === "project" && <ChallengeProject />}
        {currentTab === "hallOfFame" && <ChallengeHallOfFame />}
      </div>
    </Layout>
  )
}
