import React from "react";
import { FormattedMessage } from "react-intl";
import { dateFormatter } from "../../../../Common/Util/DateFormatter";
import closeIcon from "../../../../Image/learn-detail-close-icon.svg";
import downloadIcon from "../../../../Image/download-icon.svg";
import downloadIconGray from "../../../../Image/download-icon-gray.svg";
import downloadIconBlue from "../../../../Image/download-icon-blue.svg";
import viewMoreIcon from "../../../../Image/view-more.svg";
import fullscreenIcon from "../../../../Image/fullscreen-icon.svg";
import "./index.scss";

export default function View(props) {
  const {
    userId,
    project,
    currentMission,
    commentValue,
    comments,
    onChangeCommentValue,
    onClickSubmitComment,
    onClickMore,
    onClickReportComment,
    onClickDeleteComment,
    onClickClose,
    onClickStart,
    onClickCert
  } = props;

  if(!project) {
    return <div className="learn_detail" />
  }

  return (
    <div className="learn_detail__overlay" onClick={onClickClose}>
      <div 
        className="learn_detail" 
        onClick={(e) => { 
          e.stopPropagation(); 
        }}
      >
        <Information project={project} onClickClose={onClickClose} />
        <Game project={project} />
        <Start onClickStart={onClickStart} />
        <Progress onClickStart={onClickStart} onClickCert={onClickCert} project={project} currentMission={currentMission} />
        {/* 운영 정책상 댓글기능 임시 폐쇄 */}
        {/* <CommentWrite commentValue={commentValue} onChangeCommentValue={onChangeCommentValue} onClickSubmitComment={onClickSubmitComment} /> */}
        {/* <Comments comments={comments} userId={userId} onClickMore={onClickMore} onClickReportComment={onClickReportComment} onClickDeleteComment={onClickDeleteComment} /> */}
      </div>
    </div>
  );
}

const Information = props => {
  const { project, onClickClose } = props;
  return (
    <div className="information">
      <div className="information__header__subtitle">{project.grade}</div>
      <div className="information__header">
        <div className="information__header__title">{project.title}</div>
        <a className="information__header__tool" target="_blank" rel="noopener noreferrer" href={project.toolURL}>
          <FormattedMessage id="ID_LEARN_DETAIL_TOOL" />
          <img src={downloadIcon} alt="download"/>
        </a>
        <img className="information__header__close" src={closeIcon} alt="close" onClick={onClickClose}/>
      </div>
    </div>
  );
}

const Game = props => {
  const { project } = props;
  return (
    <div className="game">
      {/* TODO : 가로, 세로형 게임 구분을 위해 임시로 세로형 게임을 project id를 활용해 구분함, 추후 변경 필요 */}
      <div className={`game__wrapper ${project.id === 4 || project.id === 6 ? "game--vertical" : "game--horizontal"}`}>
        <iframe title={project.title} className="game__item" src={project.gameURL} scrolling="no"/>
        <div className="game__fullscreen-button">
          <img src={fullscreenIcon} alt="fullscreen"/>
        </div>
      </div>
    </div>
  );
}

const Start = props => {
  const { onClickStart } = props;
  return (
    <div className="start">
      <div className="start__button" onClick={()=> onClickStart()}><FormattedMessage id="ID_LEARN_DETAIL_START" /></div>
      <div className="start__hint"><FormattedMessage id="ID_LEARN_DETAIL_HINT" /></div>
    </div>
  );
}

const Progress = props => {
  const { project, currentMission, onClickStart, onClickCert } = props;
  const missionCount = project.missionCount;
  return (
    <>
    <div className="progress progress--pc-view">
      {[...new Array(missionCount)].map((val, i)=>{
        let className = "progress__item";
        if(i <= currentMission) {
          className += " progress__item_done";
        }
        return (
          <div onClick={()=> onClickStart(i)} key={i} className={className} style={{ width: `calc(100% / ${missionCount})`}}>
            <span className="progress__item__mission-num">{i+1}</span>
            <span className={`progress__item__line ${i === currentMission - 1 && i !== missionCount - 1 ? "line--hidden" : ""}`}/>
          </div>
        );
      })}
      {/* TODO : 인증서 다운로드 받을 수 있으면 progress--clear 클래스 추가, 이미지 downloadIconBlue로 변경 */}
      <div className="progress__item_cert" onClick={onClickCert}>
        <img src={false ? downloadIconBlue : downloadIconGray} alt="certification" />
      </div>
    </div>

    <div className="progress progress--mobile-view">
      <div className="progress__wrapper">
        {[...new Array(missionCount)].map((val, i)=>{
          let className = "progress__item";
          if(i <= currentMission) {
            className += " progress__item_done";
          }
          if(i + 1 > Math.ceil(missionCount / 2)) return null;
          return (
            <div key={i} className={className} onClick={()=> onClickStart(i)} style={{ width: `${Math.ceil(missionCount / 2) - 1 === i ? "30px" : `calc((100% - 30px) / ${Math.ceil(missionCount / 2) - 1})`}`}}>
              <span className="progress__item__mission-num">{i+1}</span>
              <span className={`progress__item__line ${i === currentMission - 1 ? "line--hidden" : ""}`}/>
            </div>
          );
        })}
      </div>
      <div className="progress__wrapper">
        {[...new Array(missionCount)].map((val, i)=>{
          let className = "progress__item";
          if(i<currentMission) {
            className += " progress__item_done";
          }
          if(i + 1 < missionCount / 2 + 1 ) return null;
          return (
            <div key={i} className={className} onClick={()=> onClickStart(i)} style={{ width: `${missionCount - 1 === i ? "30px" : `calc((100% - 30px) / ${Math.floor(missionCount / 2) - 1})`}`}}>
              <span className="progress__item__mission-num">{i+1}</span>
              <span className={`progress__item__line ${i === currentMission - 1 ? "line--hidden" : ""}`}/>
            </div>
          );
        })}
      </div>
      {/* TODO : 인증서 다운로드 받을 수 있으면 progress--clear 클래스 추가, 이미지 downloadIconBlue로 변경 */}
      <div className="progress__item_cert" onClick={onClickCert}>
        <img src={false ? downloadIconBlue : downloadIconGray} alt="certification" />
        <p><FormattedMessage id="ID_LEARN_DETAIL_CERT_DOWNLOAD" /></p>
      </div>
    </div>
    </>
  );
}

const CommentWrite = props => {
  const { commentValue, onChangeCommentValue, onClickSubmitComment } = props;
  return (
    <div className="comment_write">
      <textarea className="comment_write__textarea" onChange={onChangeCommentValue} value={commentValue} />
      <div className="comment_write__textarea__info">
        <FormattedMessage id="ID_LEARN_DETAIL_COMMENT_LENGTH" values={{length: commentValue.length}} />
      </div>
      <div className="comment_write__submit" onClick={onClickSubmitComment}>
        <FormattedMessage id="ID_LEARN_DETAIL_COMMENT_SUBMIT" />
      </div>
    </div>
  );
}

const Comments = props => {
  const { comments, userId, onClickMore, onClickReportComment, onClickDeleteComment } = props;
  return (
    <div className="comments">
      {comments.map((comment, i)=>{
        return (
          <div className="comment_item" key={i}>
            <img className="comment_item__user_icon" src={comment.user.icon} alt="user" />
            <div className="comment_item__content">
              <div className="comment_item__content__info">
                <div className="comment_item__content__info__name">{comment.user.name}</div>
                <div className="comment_item__content__info__date">{dateFormatter(comment.createdAt)}</div>
              </div>
              {userId === comment.user.id ? (
                //comment written from login user
                <div className="comment_item__content__report" onClick={()=> onClickDeleteComment(comment.id)}>
                  <FormattedMessage id="ID_LEARN_DETAIL_COMMENT_DELETE" />
                </div>
              ) : (
                <div className="comment_item__content__report" onClick={()=> onClickReportComment(comment.id)}>
                  <FormattedMessage id="ID_LEARN_DETAIL_COMMENT_REPORT" />
                </div>
              )}
              <div className="comment_item__content__text">{comment.content}</div>
            </div>
          </div>
        );
      })}
      <div className="comments__more" onClick={onClickMore}>
        <img src={viewMoreIcon} alt="view more"/>
        <FormattedMessage id="ID_LEARN_DETAIL_COMMENT_MORE" />
      </div>
    </div>
  );
}
