import React from "react";
import { injectIntl, FormattedMessage } from "react-intl";
import closeImg from "../../../Image/builder/x-copy-3.svg";
import closeImg_darkmode from "../../../Image/builder/x-copy-3_darkmode.svg";
import questionImg from "../../../Image/builder/help-circle-copy-4.svg";
// import SquareCheckbox from "../Checkbox/SquareCheckbox";
import { getColorTheme } from "../../../Page/Builder/utils/colorThemeUtil";
import Select from 'react-select';


const View = props => {
  const {
    name,
    description,
    tags,
    isCopyAllowed,
    icon,
    isDeveloping,
    handleInputChange,
    handleIsCopyAllowedChange,
    handleImgClick,
    handleAddClick,
    handleSubmit,
    handleCloseBtn,
    handleFileInput,
    handleFileDelete,
    onAddTag,
    onDeleteTag,
    popularTags,
    // handleChallengeEvent,
    // challengeEventAttend,
    nameValidation,
    descriptionValidation,

    //for camp
    isSsafy,
    onChangeType,
    onChangeOrganization,
    types,
    organizations,
    campType,
    campOrganization
  } = props;
  const colorTheme = getColorTheme();
  const campTypeValue = campType ? campType : types[0];
  const campOrganizationValue = campOrganization ? campOrganization : organizations[0];

  const customStyles = {
    container: (provided, state)=> ({
      ...provided,
      minWidth: "106px",
      width: state.selectProps.width,
      height: "40px",
      borderRadius: "6px",
      fontSize: "13px",
      whiteSpace: "nowrap"
    }),
    indicatorSeparator: () => ({
      display: "none"
    }),
    menu: (provided, state) => ({
      ...provided,
      padding: "0 14px",
      color: "#AFAFAF",
      backgroundColor: colorTheme === "darkMode" ? "#272B37" : "#fcfcfc",
      border: "none",
    }),
    option: (provided, state) => ({
      ...provided,
      width: "139px",
      padding: "5px 0",
      color: state.isSelected ? "#DF1C69" : "#AFAFAF",
      backgroundColor: colorTheme === "darkMode" ? "#272B37" : "#fcfcfc",
      cursor: "pointer",
      ":hover": {
        color: colorTheme === "darkMode" ? "#fff" : "#000",
      },
      ":active": {
        backgroundColor: "transparent",
        color: "#FFFFFF"
      }
    }),
    menuList: provided => ({
      ...provided,
      position: "relative",
      maxHeight: "194px",
      overflowY: "scroll"
    }),
    control: provided => ({
      ...provided,
      boxShadow: "none",
      backgroundColor: colorTheme === "darkMode" ? "#272B37" : "#fcfcfc",
      border: colorTheme === "darkMode" ? "none" : "1px solid #e5e6e8",
      padding: 0,
      margin: "6px 0",
      ":active": { outline: "none" },
      ":focus": { outline: "none" },
      ":hover": {
        cursor: "pointer",
      }
    }),
    valueContainer: provided => ({ 
      ...provided,
      height: "40px"
    }),
    singleValue: (provided) => ({
      ...provided,
      color: "#888888"
    })
  }

  return (
    <div className="publishpopup">
      <div className="publishpopup__header">
        <div className="header__title">
          {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP" })}
        </div>
        <img
          className="popup__closebtn"
          onClick={handleCloseBtn}
          src={colorTheme === "darkMode" ? closeImg_darkmode : closeImg}
          alt="img"
        />
      </div>
      <form className="publishform">
        {isSsafy && (
          <div className="publishform__row publishform__row__ssafy">
            <div>
              <p><FormattedMessage id="ID_CAMP_PROJECTS_ORG" /></p>
              <Select
                name="organization"
                styles={customStyles}
                onChange={onChangeOrganization}
                value={{ value: campOrganizationValue, label: campOrganizationValue }}
                options={organizations.map(item=> { return { value: item, label: item }})}
              />
            </div>
            <div>
              <p><FormattedMessage id="ID_CAMP_PROJECTS_TYPE" /></p>
              <Select
                name="type"
                styles={customStyles}
                onChange={onChangeType}
                value={{ value: campTypeValue, label: campTypeValue }}
                options={types.map(item=> { return { value: item, label: item }})}
              />
            </div>
          </div>
        )}
        {/* name */}
        <div className="publishform__row">
          <p className="publishform__title">
            {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP_TITLE" })}
          </p>

          <input
            type="text"
            name="name"
            value={name}
            placeholder={props.intl.formatMessage({
              id: "ID_BUILDER_EDITPOP_TITLE_PH"
            })}
            onChange={handleInputChange}
            className={`publishform__input ${!nameValidation &&
              "validationFalse"}`}
            autoComplete="off"
          />
          {!nameValidation && (
            <p className="publishform__validation_check">
              {props.intl.formatMessage({
                id: "ID_BUILDER_EDITPOP_TITLE_VALIDATION"
              })}
            </p>
          )}
        </div>
        {/* desc */}
        <div className="publishform__row">
          <p className="publishform__title">
            {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP_DESC" })}
          </p>

          <textarea
            type="text"
            name="description"
            value={description}
            onChange={handleInputChange}
            className={`publishform__textarea ${!descriptionValidation &&
              "validationFalse"}`}
            placeholder={props.intl.formatMessage({
              id: "ID_BUILDER_EDITPOP_DESC_PH"
            })}
          />
          {!descriptionValidation && (
            <p className="publishform__validation_check">
              {props.intl.formatMessage({
                id: "ID_BUILDER_EDITPOP_DESC_VALIDATION"
              })}
            </p>
          )}
        </div>

        {/* icon */}
        {icon && (
          <div className="publishform__row">
            <p className="publishform__title">
              {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP_ICON" })}
            </p>

            <div className="publishform__icon">
              <img
                className="publishform__icon__img"
                src={icon}
                alt="icon"
                onClick={handleImgClick}
              />
              <div className="publishform__icon__buttons">
                {isDeveloping ? (
                  <React.Fragment>
                    <div
                      className="publishform__icon__button"
                      onClick={handleAddClick}
                    >
                      {props.intl.formatMessage({
                        id: "ID_BUILDER_EDITPOP_IMAGE_CHANGE"
                      })}
                    </div>
                    <div
                      className="publishform__icon__button"
                      onClick={handleFileDelete}
                    >
                      {props.intl.formatMessage({
                        id: "ID_BUILDER_EDITPOP_IMAGE_DELETE"
                      })}
                    </div>
                  </React.Fragment>
                ) : (
                  <div
                    className="publishform__icon__button"
                    onClick={handleAddClick}
                  >
                    {props.intl.formatMessage({
                      id: "ID_BUILDER_EDITPOP_IMAGE_CHANGE"
                    })}
                  </div>
                )}
              </div>
              <input
                id="PublishForm__icon__input"
                className="publishform__icon__input"
                type="file"
                accept=".jpg,.jpef,.png"
                onChange={handleFileInput}
              />
            </div>
          </div>
        )}

        {/* tag */}
        <div className="publishform__row">
          <div className="publishform__title__wrapper">
            <p className="publishform__title publishform__title__tag">
              {props.intl.formatMessage({
                id: "ID_BUILDER_EDITPOP_TAG"
              })}
            </p>
            <img
              src={questionImg}
              className="questionImg"
              data-tip={props.intl.formatMessage({
                id: "ID_BUILDER_EDITPOP_TIP_TAG"
              })}
              data-place="right"
              alt="publish question"
            />
          </div>
          <div className="tag--added">
            {tags.map((item, index) => {
              return (
                <span
                  key={index}
                  className="tag--added__item"
                  onClick={() => {
                    onDeleteTag(index);
                  }}
                >{`#${item}`}</span>
              );
            })}
          </div>
          <input
            type="text"
            name="tag"
            placeholder={props.intl.formatMessage({
              id: "ID_BUILDER_EDITPOP_TAG_PLACEHOLDER"
            })}
            onChange={handleInputChange}
            className="publishform__input tag"
            autoComplete="off"
            onKeyDown={e => {
              const tag = e.target.value;
              if (e.keyCode === 13 && tag !== "") {
                onAddTag(tag);
                e.target.value = "";
              } else if (e.keyCode === 8 && tag === "" && tags.length > 0) {
                onDeleteTag(tags.length - 1);
              }
            }}
          />
          <div className="tag--recommend">
            {popularTags.map((item, index) => {
              return (
                <span
                  key={index}
                  className="tag--recommend__item"
                  onClick={() => onAddTag(item)}
                >{`#${item}`}</span>
              );
            })}
          </div>
        </div>

        {/* code open */}
        <div className="publishform__row">
          <div className="publishform__title__wrapper">
            <p className="publishform__title">
              {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP_CODE" })}
            </p>
            <img
              src={questionImg}
              className="questionImg"
              data-tip={props.intl.formatMessage({
                id: "ID_BUILDER_EDITPOP_TIP_CODE"
              })}
              data-place="right"
              alt="publish question"
            />
          </div>

          {isCopyAllowed !== undefined ? (
            <div className="publishform__checkbox">
              <label>
                <input
                  type="radio"
                  name="copyAllowed"
                  value="on"
                  checked={isCopyAllowed}
                  onChange={handleIsCopyAllowedChange}
                />
                <span className="checkbox__mark" />
                <span className="checkbox__text">
                  {props.intl.formatMessage({
                    id: "ID_BUILDER_EDITPOP_CODE_OPEN"
                  })}
                </span>
              </label>
              <label>
                <input
                  type="radio"
                  name="copyAllowed"
                  value="off"
                  checked={!isCopyAllowed}
                  onChange={handleIsCopyAllowedChange}
                />
                <span className="checkbox__mark" />
                <span className="checkbox__text">
                  {props.intl.formatMessage({
                    id: "ID_BUILDER_EDITPOP_CODE_CLOSE"
                  })}
                </span>
              </label>
            </div>
          ) : null}
        </div>

        {/* sumit */}
        <div className="publishform__row publishform__row--sumit">
          <div className="publishbtn" onClick={handleSubmit}>
            <span>
              {props.intl.formatMessage({ id: "ID_BUILDER_EDITPOP_UPLOAD" })}
            </span>
          </div>
        </div>
      </form>
    </div>
  );
};

export default injectIntl(View);
