import React from "react";
import "./index.scss";

import Game2dIcon from "../../../../../../Image/icon-2d.svg";
import Game3dIcon from "../../../../../../Image/icon-3d.svg";
import projectDetailIcon from "../../../../../../Image/icon-more.svg";
import editIcon from "../../../../../../Image/icon-edit.svg";
import viewAppIcon from "../../../../../../Image/icon-view-app.svg";
import copyIcon from "../../../../../../Image/copy.svg";
import deleteIcon from "../../../../../../Image/delete.svg";
import moreIcon from "../../../../../../Image/icon_more_all.svg";
import project3DLabel from "../../../../../../Image/3d-label.svg";
import { is3DProject } from "../../../../Container";

export default function View(props) {
  const {
    onClick2DGame,
    onClick3DGame,
    onClickMyProjectMore,
    onClickMyPublishedMore,
    onClickDetailBtn,
    onClickMyApkMore,
    onClickProject,
    onClickPublishProject,
    onClickProjectEdit,
    handleEdit,
    handleDelete,
    handleCopy,
    selectProject,
    myProjects,
    myPublisheds,
    myApks,
    fetchMyProjects,
    setProjectName
  } = props;

  return (
    <div className="builder--home__main">
      <div className="main__top">
        <div className="main__making">
          <div className="main__title">새로 만들기</div>
          <div className="making__items">
            <div className="making__item" onClick={onClick3DGame}>
              <img src={Game3dIcon} alt="3d game" />
              <p>3D World</p>
            </div>
            <div className="making__item" onClick={onClick2DGame}>
              <img src={Game2dIcon} alt="2d game" />
              <p>2D World</p>
            </div>
          </div>
        </div>
      </div>
      <div className="main__row row__myProjects">
        <div className="row__header">
          <div className="main__title">최근 프로젝트</div>
          <div onClick={onClickMyProjectMore} className="myProjects__more">
            더보기 <img src={moreIcon} alt="" />
          </div>
        </div>
        <div className="project__Items">
          {myProjects.length ? (
            myProjects.map((item, index) => {
              return (
                <ProjectItem
                  project={item}
                  onClickProject={onClickProject}
                  onClickDetailBtn={onClickDetailBtn}
                  selectProject={selectProject}
                  handleEdit={handleEdit}
                  handleDelete={handleDelete}
                  handleCopy={handleCopy}
                  key={index}
                  fetchMyProjects={fetchMyProjects}
                  setProjectName={setProjectName}
                />
              );
            })
          ) : (
            <div className="project__Items no__Items">
              최근 프로젝트가 없습니다.
            </div>
          )}
        </div>
      </div>
      <div className="main__row row__myPublished">
        <div className="row__header">
          <div className="main__title">퍼블리싱 앱</div>
          <div onClick={onClickMyPublishedMore} className="myPublished__more">
            더보기 <img src={moreIcon} alt="" />
          </div>
        </div>

        <div className="project__Items">
          {myPublisheds.length ? (
            myPublisheds.map((item, index) => {
              return (
                <ProjectItem
                  project={item}
                  onClickProject={onClickProject}
                  onClickPublishProject={onClickPublishProject}
                  onClickDetailBtn={onClickDetailBtn}
                  handleEdit={handleEdit}
                  onClickProjectEdit={onClickProjectEdit}
                  selectProject={selectProject}
                  key={index}
                  type="published"
                />
              );
            })
          ) : (
            <div className="project__Items no__Items">
              퍼블리싱한 앱이 없습니다.
            </div>
          )}
        </div>
      </div>
      <div className="main__row row__myApk">
        <div className="row__header">
          <div className="main__title">나의 AAB</div>
          <div onClick={onClickMyApkMore} className="myApk__more">
            더보기 <img src={moreIcon} alt="" />
          </div>
        </div>
        <div className="project__Items">
          {myApks.length ? (
            myApks.map((item, index) => {
              return (
                <ApkItem
                  apk={item}
                  onClickProject={onClickProject}
                  key={index}
                />
              );
            })
          ) : (
            <div className="project__Items no__Items">
              제작한 AAB가 없습니다.
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

const ProjectItem = props => {
  const {
    project,
    onClickProject,
    onClickPublishProject,
    onClickDetailBtn,
    onClickProjectEdit,
    selectProject,
    handleEdit,
    handleDelete,
    handleCopy,
    fetchMyProjects,
    type,
    setProjectName
  } = props;

  const getTypeFromIconUrl = project => {
    return project.icon.includes("redbrick") ? "3D" : project.type;
  }

  return (
    <div className="projectItem">
      <div
        className="projectItem__top"
        onClick={e => {
          e.preventDefault();
          onClickProject(project.pId, getTypeFromIconUrl(project));
        }}
      >
        <img className="top__img" src={project.icon} alt="project" />
        {is3DProject(project) && <img className="projectItem__3d-label" src={project3DLabel} alt="3d-label" />}
      </div>
      <div
        className="projectItem__bottom"
        onClick={e => {
          e.preventDefault();
          onClickProject(project.pId, getTypeFromIconUrl(project));
        }}
      >
        <div className="bottom__title">{project.name}</div>
        <div className="bottom__time">
          {project.updatedAt &&
            project.updatedAt.split("T")[0].replaceAll("-", ".")}
        </div>
      </div>
      <div
        className={`projectItem__detail ${selectProject.pId === project.pId &&
          selectProject.type === type &&
          "selected"}`}
        onClick={() => {
          onClickDetailBtn(project.pId, type);
        }}
      >
        <img src={projectDetailIcon} alt="project Detail Icon" />
        <ul className="projectItem__detail__list">
          {type === "published" ? (
            <>
              <li onClick={() => onClickProjectEdit(project)}>
                <img src={editIcon} alt="edit" />
                수정
              </li>
              <li
                onClick={() => {
                  if (project.live) onClickPublishProject(project.pId);
                }}
              >
                <img src={viewAppIcon} alt="" />앱 보기
              </li>
            </>
          ) : (
            <>
              <li
                onClick={() =>
                  handleEdit(project.pId, project.name, setProjectName)
                }
              >
                <img src={editIcon} alt="edit" />
                수정
              </li>
              <li
                onClick={() => {
                  handleDelete(project.pId, fetchMyProjects);
                }}
              >
                <img src={deleteIcon} alt="" />
                삭제
              </li>
              <li
                onClick={() => {
                  handleCopy(project.pId, fetchMyProjects);
                }}
              >
                <img src={copyIcon} alt="" />
                복사
              </li>
            </>
          )}
        </ul>
      </div>
    </div>
  );
};

const ApkItem = props => {
  const { apk, onClickProject } = props;
  return (
    <div
      className="projectItem"
      onClick={() => {
        onClickProject(apk.pId);
      }}
    >
      <div className="projectItem__top">
        <img className="top__img" src={apk.apkVersion.appIcon} alt="app" />
      </div>
      <div className="projectItem__bottom">
        <div className="bottom__title">{apk.apkVersion.appName}</div>
      </div>
    </div>
  );
};
