import React from "react";
import "./index.scss";
import { injectIntl } from "react-intl";
import Layout from "../../Common/Component/Layout";
import ChangePassword from "./ChangePassword";
import authenticationCompletedIcon from "../../Image/verification-completed.svg"
import authenticationErrorIcon from "../../Image/authentication-error.svg"
import checkActive from "../../Image/signup/check-active.svg";
import checkInactive from "../../Image/signup/check-inactive.svg";
import { MY_ACCOUNT_TABS, MY_ACCOUNT_FIELD } from "./Common/Constant";

function View(props) {
  const { 
    intl, 
    selectedTab, 
    isVerifyBirth, 
    isVerifyEmail, 
    userInfo, 
    onClickTab, 
    onChangeGender,
    onClickAgreementMarketing,
    onClickLeave,
    onClickRegister,
    pendingEmail
  } = props
  return (
    <Layout>
      <div className="my-account">
        <div className="my-account__title">{intl.formatMessage({ id: "ID_MY_ACCOUNT_TITLE" })}</div>
        <div className="my-account__tabs">
          <div className= {`my-account__tabs__account ${selectedTab === MY_ACCOUNT_TABS.MANAGE && "my-account__tabs--selected"}`}
              onClick={()=>onClickTab(MY_ACCOUNT_TABS.MANAGE)}>
            {intl.formatMessage({ id: "ID_MY_ACCOUNT_TAB_MANAGE" })}
          </div>
          <div className="my-account__tabs__line"/>
          <div className={`my-account__tabs__password ${selectedTab === MY_ACCOUNT_TABS.CHANGE_PASSWORD && "my-account__tabs--selected"}`}
              onClick={()=>onClickTab(MY_ACCOUNT_TABS.CHANGE_PASSWORD)}>
            {intl.formatMessage({ id: "ID_MY_ACCOUNT_TAB_CHANGE_PW" })}
          </div>
        </div>

        { selectedTab === MY_ACCOUNT_TABS.MANAGE ?
        (
          <>
            <AccountManageView 
              intl={intl}
              userInfo={userInfo}
              isVerifyBirth={isVerifyBirth}
              isVerifyEmail={isVerifyEmail}
              onChangeGender={onChangeGender}
              onClickRegister={onClickRegister}
              pendingEmail={pendingEmail}
            />

            <div className="my-account__agreement-marketing" onClick={()=>onClickAgreementMarketing(userInfo.isMarketingAgreement)}>
              <img src={userInfo.isMarketingAgreement ? checkActive : checkInactive} alt="check" />
              <p>{intl.formatMessage({ id: "ID_MY_ACCOUNT_MARKETING" })}</p>
            </div>
            <div className="my-account__leave" onClick={onClickLeave}>{intl.formatMessage({ id: "ID_MY_ACCOUNT_LEAVE" })}</div>

          </>
        ) : <ChangePassword />
      }
      </div>
    </Layout>
  )
}




function AccountManageView(props){
  const { 
    intl, 
    userInfo,
    isVerifyBirth, 
    isVerifyEmail,
    onChangeGender, 
    onClickRegister,
    pendingEmail
  } = props
  const { gender, phone, email, birth } = userInfo

  return(
    <div className="my-account__manage">
      <Field
        intl={intl} 
        fieldType={MY_ACCOUNT_FIELD.ID}
        title={intl.formatMessage({ id: "ID_MY_ACCOUNT_ID" })}
        value= {email}/>

      <Field
        intl={intl} 
        fieldType={MY_ACCOUNT_FIELD.BIRTH}
        title={intl.formatMessage({ id: "ID_MY_ACCOUNT_BIRTH" })}
        isVerify={isVerifyBirth}
        onClickRegister={!birth && onClickRegister}
        value={birth && birth.replaceAll("-", ".")}/>

      <Field
        intl={intl} 
        fieldType={MY_ACCOUNT_FIELD.EMAIL}
        title={intl.formatMessage({ id: "ID_MY_ACCOUNT_EMAIL" })}
        isVerify={isVerifyEmail}
        onClickRegister={onClickRegister}
        value={userInfo.verifiedEmail ? userInfo.verifiedEmail : pendingEmail}/>

      <Field
        intl={intl}
        fieldType={MY_ACCOUNT_FIELD.PHONE}
        title={intl.formatMessage({ id: "ID_MY_ACCOUNT_PHONE" })}
        isVerify={phone ? true : undefined}
        onClickRegister={onClickRegister}
        value={phone}/>

      <GenderField
        intl={intl} 
        fieldType={MY_ACCOUNT_FIELD.GENDER}
        name="gender"
        title={intl.formatMessage({ id: "ID_MY_ACCOUNT_GENDER" })}
        onChangeGender={onChangeGender}
        gender={gender}/>
    </div>


  )
}


function Field(props){
  const { intl, fieldType, title, value, isVerify, onClickRegister } = props
  return (
    <div className="my-account__manage__field">
      <div className="my-account__manage__field__title">{title}</div>
      <div className="my-account__manage__field__text">{value}</div>
          {
            fieldType !== MY_ACCOUNT_FIELD.BIRTH && isVerify !== undefined && 
            (
              <div className="my-account__manage__field__auth-info">
                <img src={ isVerify ? authenticationCompletedIcon : authenticationErrorIcon } alt="check img"/>
                <div className={ isVerify ?  "my-account__manage__field__auth-info__completed" : "my-account__manage__field__auth-info__need"}>
                  { isVerify ? intl.formatMessage({ id: "ID_MY_ACCOUNT_VERIFY_COMPLETE" }) : intl.formatMessage({ id: "ID_MY_ACCOUNT_VERIFY_NEED" }) }
                </div>
              </div>
            )
          }
           {  onClickRegister && 
              <button onClick={()=>{ onClickRegister(fieldType) }}>
                {value ? intl.formatMessage({ id: "ID_MY_ACCOUNT_CHANGE"}) : intl.formatMessage({ id: "ID_MY_ACCOUNT_REGISTER"})}
              </button>
           }
    </div>
  )
}

function GenderField(props) {
  const { intl, gender, onChangeGender } = props
  return (
    <RadioField
      {...props}
      value={gender || "N"}
      onChange={onChangeGender}
      options={[
        { value: "F", label: intl.formatMessage({ id: "ID_MY_ACCOUNT_WOMAN" }) },
        { value: "M", label: intl.formatMessage({ id: "ID_MY_ACCOUNT_MAN" }) },
        { value: "N", label: intl.formatMessage({ id: "ID_MY_ACCOUNT_NOT_SELECT" }) }
      ]}
    />
  );
}

function RadioField(props){
  const { title, name, value, onChange } = props
  return (
    <div className="my-account__manage__field border-bottom--none">
      <div className="my-account__manage__field__title">{title}</div>
      <div className="my-account__manage__field__radio_containers">
        {props.options.map(option => (
          <div
            key={option.value}
            className="my-account__manage__field__radio_container"
          >
            <input
              type="radio" 
              id={option.value}
              name={name}
              value={option.value}
              checked={option.value === value}
              onChange={()=>onChange(option.value)}
            />
            <label
              htmlFor={option.value}
            >
              <span className={`my-account__manage__field__radio-handle checked--${option.value === value}`}>
                <span className="checked"></span>
              </span>
              {option.label}
            </label>
          </div>
        ))}
      </div>
    </div>
  )
}

export default injectIntl(View)
