import React from "react";
import * as request from "../../../../Common/Util/HTTPRequest";
import View from "./View";

class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      weeklyTops: [],
      showsMore: true
    }
  }
  componentDidMount() {
    this.loadMoreWeeklyTops(5);
  }
  loadMoreWeeklyTops(limit) {
    request
      .getWeeklyProjectsByType({
        type: "like",
        offset: this.state.weeklyTops.length,
        limit: limit,
      })
      .then(res => res.json())
      .then(weeklyTops => {
        this.setState(prevState => ({
          weeklyTops: [].concat(prevState.weeklyTops, weeklyTops)
        }));
      })
  }

  onClickMore = () => {
    this.setState({ showsMore: false }, () => {
      this.loadMoreWeeklyTops(10);
    })
  }

  render() {
    return (
      <View
        {...this.state}
        onClickMore={this.onClickMore}
      />
    );
  }
}

export default Container;
