import React from "react";
import Layout from "../../Common/Component/Layout";
import Tabs from "./Components/Tabs";
import EventItem from "./Components/EventItem";
import NewsTable from "./Components/NewsTable";
import { events, results } from "./Common/eventData";
import "./index.scss";

export default function View(props) {
  const {
    intl,
    TAB_TYPE,
    selectedTab,
    news,
    newsCount,
    onClickTab,
    onClickMore
  } = props;

  let content;
  switch (selectedTab) {
    case TAB_TYPE.ING:
      content = (
        <div className="news__items__item">
          {events.length === 0 ? (
            <p className="news-event-text">{intl.formatMessage({ id: "ID_EVENT_PLACEHOLDER" })}</p>
          ) : (
            events.map((event, i) => (
              <EventItem event={event} key={i} intl={intl}/>
            ))
          )}
        </div>
      );
      break;
    case TAB_TYPE.END:
      content = (
        <div className="news__items__item">
          {results.map((result, i) => (
            <EventItem event={result} key={i} intl={intl} />
          ))}
        </div>
      );
      break;
    case TAB_TYPE.NOTICE:
      content = (
        <NewsTable 
          news={news}
          newsCount={newsCount}
          onClickMore={onClickMore}
        />
      )
      break;
    default:
      break;
  }

  return (
    <Layout>
      <div className="news__wrap">
        <div className="news">
          <div className="news__title">
            {intl.formatMessage({ id: "ID_EVENT_TITLE" })}
          </div>
          <Tabs 
            TAB_TYPE={TAB_TYPE}
            selectedTab={selectedTab}
            onClickTab={onClickTab}
          />
          <section className="news__items">{content}</section>
        </div>
      </div>
    </Layout>
  )
}
