import React from "react";
import { Link } from "react-router-dom";
import { FormattedMessage } from "react-intl";
import AnimationImg from "../../../../Image/home_dragon_animation.png";
import TemplateSlider from "../TemplateSlider";
import "./index.scss";

function EventSlide(props) {
  return (
    <div className="event-slide">
      <img
        className="event-slide__background-img"
        src={props.event.background}
        alt="event-slide-background"
      />
      <img
        className="event-slide__background-img event-slide__background-img--mobile"
        src={props.event.foreground}
        alt="event-slide-background"
      />
      <div className="event-slide__animationWrapper">
        <img className="event-slide__animation" src={AnimationImg} alt="event-slide-animation" />
      </div>

      <div className="event-slide__overlay" />
      <div className="event-slide__content">
        <div className="event-slide__title">
          {props.event.title}
        </div>
        <div className="event-slide__desc">
          {props.event.description}
        </div>

        <Link
          className="event-slide__link-btn"
          name={props.event.url}
          onClick={props.onClickLink}
          to={props.event.url}
        >
          {props.event.buttonName
            || <FormattedMessage id="ID_HOME_MAIN_CREATE_EVENT" />
          }
        </Link>
      </div>

      <div className="event-slide__template-overlay">
        <div class="event-slide__template__content">
          <div class="event-slide__template__title">
            <FormattedMessage id="ID_HOME_TEMPLATE_TITLE" />
          </div>

          <div class="event-slide__template__body">
            <TemplateSlider />
          </div>
        </div>
      </div>
    </div>
  )
}

export default EventSlide;
