import React from "react";
import { FormattedMessage } from "react-intl";
import Markdown from "../../../../Common/Component/Markdown";
import "./index.scss";

class Terms extends React.Component {

  onClickConfirm = () => {
    if(this.props.dismiss) {
      this.props.dismiss();
    }
    if(this.props.onClickConfirm) {
      this.props.onClickConfirm();
    }
  }

  render() {
    return (
      <div className="challenge-terms">
        <div className="challenge-terms__content">
          <Markdown url={require("./terms.md")} />
        </div>
        <button
          className="popup_button"
          onClick={this.onClickConfirm}
        >
          <FormattedMessage id="ID_CONFIRM"/>
        </button>
      </div>
    );
  }
}

export default Terms;
