import React from 'react';
import { FormattedMessage } from "react-intl";
import { Link } from "react-router-dom";
import moment from "moment";
import Layout from '../../Common/Component/Layout';
import "./index.scss";

export default function View(props) {
  const { challengeItems, onClickRemove, onClickItem, onClickEdit } = props;
  return (
    <Layout className="layout--challenge">
      <div className="challenge-myproject-list">
        <p className="challenge-myproject-list__title">
          <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_TITLE"/>
        </p>
        {challengeItems.length > 0 ? 
          <>
          <div className="challenge-myproject-list__info">
            <p className="challenge-myproject-list__info__subtitle">
              <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_SUBTITLE"/>
            </p>
            <span className="challenge-myproject-list__info__desc info-pc">
              <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_DESC_PC"/>
            </span>
            <span className="challenge-myproject-list__info__desc info-mobile">
              <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_DESC_M"/>
            </span>
          </div>
          <div className="challenge-myproject-list__items">
            {challengeItems.map((item, i)=>{
              return (
                <ProjectItem
                  item={item} 
                  key={i}
                  onClickRemove={onClickRemove}
                  onClickItem={onClickItem}
                  onClickEdit={onClickEdit}
                />
              )
            })}
          </div>
          </>
          :
          <p className="challenge-myproject-list__placeholder">
            <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_PLACEHOLDER"/>
          </p>
        }
        <Link to="/challenge2021" className="challenge-myproject-list__move-challange-button">
          <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_MOVE_BUTTON"/>
        </Link>
      </div>
    </Layout>
  )
}

const ProjectItem = ({ item, onClickRemove, onClickItem, onClickEdit }) => {
  return (
    <div 
      className="challenge-myproject-list__item" 
      onClick={(e) => {onClickItem(e, item.id)}}
    >
      <div className="challenge-myproject-list__item__section">
        <img 
          className="challenge-myproject-list__item__thumbnail" 
          src={item.project.icon} 
          alt="item thumbnail"
        />
      </div>
      <div className="challenge-myproject-list__item__section">
        <p className="challenge-myproject-list__item__title">
          {item.productTitle}
        </p>
        <p className="challenge-myproject-list__item__subtitle">
          {item.teamName}
        </p>
        <p className="challenge-myproject-list__item__date date-pc">
          <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_DATE"/>{" : "}
          {moment(item.createdAt).format("YYYY-MM-DD").replaceAll('-','.')}
        </p>
        <p className="challenge-myproject-list__item__date date-mobile">
          <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_DATE"/><br/>
          {moment(item.createdAt).format("YYYY-MM-DD").replaceAll('-','.')}
        </p>
        {/* challenge is over */}
        {/* <div className="challenge-myproject-list__item__buttons">
          <button 
            className="challenge-myproject-list__item__button edit-button"
            onClick={(e)=> onClickEdit(e, item.id)}
          >
            <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_EDIT_BUTTON"/>
          </button>
          <button 
            className="challenge-myproject-list__item__button delete-button" 
            onClick={(e)=> onClickRemove(e, item.id)}
          >
            <FormattedMessage id="ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETE_BUTTON"/>
          </button>
        </div> */}
      </div>
    </div>
  );
}
