import { GRAMMAR, BLOCK, DATA } from "./Type";

export default [
  {
    name: "set",
    parentDataTypes: [DATA.VARIABLE, DATA.NUMBER, DATA.STRING, DATA.BOOLEAN]
  },
  {
    name: "say",
    parentDataTypes: [DATA.SPRITE],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.STRING],
          [BLOCK.VARIABLE]: [DATA.NUMBER, DATA.STRING, DATA.BOOLEAN],
          [BLOCK.SPRITE]: [DATA.TEXT]
        }
      }
    ]
  },
  {
    name: "playAnimation",
    parentDataTypes: [DATA.SPRITE],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.ANIMATION]
        }
      }
    ]
  },
  {
    name: "stopAnimation",
    parentDataTypes: [DATA.SPRITE]
  },
  {
    name: "move",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.DIRECTION, DATA.AXIS]
        }
      },
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER],
          [BLOCK.VARIABLE]: [DATA.NUMBER],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN, DATA.JOYSTICK]
        }
      }
    ]
  },
  {
    name: "moveTo",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.POSITION, DATA.TOUCH, DATA.RANDOM],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      }
    ]
  },
  {
    name: "go",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.DIRECTION, DATA.AXIS]
        }
      },
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER],
          [BLOCK.VARIABLE]: [DATA.NUMBER],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN, DATA.JOYSTICK]
        }
      }
    ]
  },
  {
    name: "goTo",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.POSITION, DATA.TOUCH, DATA.RANDOM],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      }
    ]
  },
  {
    name: "turn",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER],
          [BLOCK.VARIABLE]: [DATA.NUMBER],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN, DATA.JOYSTICK]
        }
      }
    ]
  },
  {
    name: "turnTo",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.TOUCH],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      }
    ]
  },
  {
    name: "show",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT]
  },
  {
    name: "hide",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT]
  },
  {
    name: "shake",
    parentDataTypes: [DATA.SPRITE, DATA.SCREEN]
  },
  {
    name: "flash",
    parentDataTypes: [DATA.SCREEN]
  },
  {
    name: "wait",
    parentDataTypes: [DATA.SPRITE, DATA.SCREEN, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER]
        }
      }
    ]
  },
  {
    name: "kill",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT]
  },
  {
    name: "setCollision",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN]
        }
      }
    ]
  },
  {
    name: "onOverlap",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        grammars: {
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      },
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onTouch",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onTouchUp",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onOut",
    parentDataTypes: [DATA.SPRITE, DATA.TEXT],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  // {
  //   name: "show",
  //   parentDataTypes: [DATA.SCREEN]
  // },
  // {
  //   name: "hide",
  //   parentDataTypes: [DATA.SCREEN]
  // },
  {
    name: "changeScene",
    parentDataTypes: [DATA.SCREEN],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.SCREEN]
        }
      }
    ]
  },
  {
    name: "onFrame",
    parentDataTypes: [DATA.SCREEN],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onTouch",
    parentDataTypes: [DATA.SCREEN],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onTouchUp",
    parentDataTypes: [DATA.SCREEN],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "onSwipe",
    parentDataTypes: [DATA.SPRITE, DATA.SCREEN],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.DIRECTION]
        }
      },
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "bind",
    parentDataTypes: [DATA.JOYSTICK],
    childs: [
      {
        grammars: {
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.SPRITE]: [DATA.SPRITE]
        }
      },
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER]
        }
      }
    ]
  },
  {
    name: "play",
    parentDataTypes: [DATA.SOUND]
  },
  {
    name: "restart",
    parentDataTypes: [DATA.SOUND]
  },
  {
    name: "resume",
    parentDataTypes: [DATA.SOUND]
  },
  {
    name: "stop",
    parentDataTypes: [DATA.SOUND]
  },
  {
    name: "pause",
    parentDataTypes: [DATA.SOUND]
  },
  {
    name: "onShake",
    parentDataTypes: [DATA.MOBILE],
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "vibrate",
    parentDataTypes: [DATA.MOBILE]
  },
  {
    name: "speak",
    parentDataTypes: [DATA.MOBILE],
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER, DATA.STRING, DATA.BOOLEAN]
        }
      }
    ]
  }
];
