import React, { useCallback } from "react";
import noop from "lodash/noop";
import styled from "@emotion/styled";
import oobcIcon from "../../../Image/oobc_icon.png";
import jsIcon from "../../../Image/js_icon.png";
import pythonIcon from "../../../Image/python_icon.png";

const Self = styled.div`
  display: flex;
  align-items: center;
  margin: 30px 0;
  border: solid 2px rgba(255, 255, 255, 0.3);
  border-radius: 27.5px;
  padding: 10px 22px;
  color: #ffffff;
  font-size: 20px;
  font-weight: 500;
  line-height: 24px;
  letter-spacing: 0.38px;
  text-align: center;
  cursor: pointer;

  &:not(:last-child) {
    margin-right: 10px;
  }

  ${(props) =>
    props.active &&
    `
      background-color: #ff6f44;
      border-color: #ff6f44;
  `}

  @media screen and (max-width: 1169px) {
    height: 42px;
    line-height: 42px;
    padding: 0 16.5px 0 17.5px;
    margin: 0 0 10px;
    border: solid 1.5px rgba(255, 255, 255, 0.2);
    border-radius: 21px;
    font-size: 15px;
  }
`;

const Icon = styled.div`
  ${(props) => props.type && `
    position: relative;
    top: 1px;
    width: 33px;
    height: 33px;
    margin-right: 14px;

    @media screen and (max-width: 1169px) {
      width: 25px;
      height: 25px;
      margin-right: 10px;
    }
  `}

  ${(props) => 
    props.type === "oobc" && `
    background: url(${oobcIcon}) no-repeat center/cover;
  `}

  ${(props) => 
    props.type === "javascript" && `
    background: url(${jsIcon}) no-repeat center/cover;
  `}

  ${(props) => 
    props.type === "python" && `
    background: url(${pythonIcon}) no-repeat center/cover;
  `}
`;

const Category = ({ active, categoryId = "", children, onClick = noop, ...props }) => {
  const handleClick = useCallback(
    () => {
      onClick(categoryId);
    },
    [onClick]
  );

  return (
    <Self {...props} active={active} onClick={handleClick}>
      <Icon type={props.type} />
      {children}
    </Self>
  );
};

export default Category;
