import React, { Component } from "react";
import { Link } from "react-router-dom";
import ChallengeIntro from "./components/ChallnegeIntro";
import ChallengeWorks from "./components/ChallengeWorks";
import Layout from "../../Common/Component/Layout";
import "./index.scss";

export default class View extends Component {
  render() {
    const { onCilckTab, currentTab } = this.props;
    return (
      <Layout>
        <div className="challenge">
          <div className="challenge__banner">
            <div className="challenge__banner__wrap">
              <div className="challenge__banner__title" />
              {/* challenge 처리 */}
              <Link to="/course/16">
                <div className="challenge__banner__button" />
              </Link>
            </div>
          </div>
          <div className="challenge__wrap">
            <ul className="challenge__tabs">
              <li 
                className={ currentTab === "intro" ? "active" : ""}
                onClick={() => onCilckTab('intro')}
              >
                행사 소개
              </li> 
              <li
                className={ currentTab === "works" ? "active" : ""}
                onClick={() => onCilckTab('works')}>참여 작품</li>
            </ul>

            {currentTab === "intro" && <ChallengeIntro />}
            {currentTab === "works" && <ChallengeWorks />}
          </div>
        </div>
      </Layout>
    )
  }
}
