import React from "react";
import { FormattedMessage } from "react-intl";
import moment from "moment";
import Layout from "../../Common/Component/Layout";
import Comment from "./Comment";
import prevIcon from "../../Image/icon-new-prev.svg";
import nextIcon from "../../Image/icon-new-next.svg";
import htmlparser from "react-html-parser";
import "./index.scss";

export default function index(props) {
  const {
    id,
    email,
    topic,
    title,
    comments,
    nextNews,
    previousNews,
    createdAt,
    description,
    onClickHome,
    onClickPrevNews,
    onClickNextNews,
    onClickNewReply,
    onAddNewComment
  } = props;

  return (
    <Layout>
      <div className="news-detail">
        <div className="news__title">
          <FormattedMessage id="ID_EVENT_TITLE" />
        </div>
        <div className="news-detail__header pc">
          <div className="news-detail__header__title">
            [{topic}] {title}
          </div>
          <div className="news-detail__header__created-at">
            {moment(createdAt).format("YYYY.MM.DD")}
          </div>
        </div>
        <div className="news-detail__header mobile">
          <div className="news-detail__header__title">{title}</div>
          <div className="news-detail__header__bottom">
            <div className="news-detail__header__topic">{topic}</div>
            <div className="news-detail__header__created-at">
              {moment(createdAt).format("YYYY.MM.DD")}
            </div>
          </div>
        </div>
        <div className="news-detail__content">
          {htmlparser(description)}
        </div>
        <div className="news-detail__list">
          {previousNews && (
            <div onClick={onClickPrevNews}>
              <img src={prevIcon} alt="prev" />
              <span>{previousNews.topic}</span>
              {previousNews.title}
            </div>
          )}
          {nextNews && (
            <div onClick={onClickNextNews}>
              <img src={nextIcon} alt="next" />
              <span>{nextNews.topic}</span>
              {nextNews.title}
            </div>
          )}
        </div>
        <div className="news-detail__buttons">
          {previousNews && (
            <div onClick={onClickPrevNews}>
              <FormattedMessage id="ID_NEWS_PREV_BUTTON" />
            </div>
          )}
          {nextNews && (
            <div onClick={onClickNextNews}>
              <FormattedMessage id="ID_NEWS_NEXT_BUTTON" />
            </div>
          )}
          <div className="list-button" onClick={onClickHome}>
            <FormattedMessage id="ID_NEWS_LIST_BUTTON" />
          </div>
        </div>
      </div>

      {
        topic !== "공지사항" &&
          <div className="news-detail__comment">
            <p className="news-detail__comment__length">
              <FormattedMessage id="ID_COMMENT_REVIEW"/> {comments.length}
            </p>
            {
              email 
                ? <Comment newsId={id} onAddNewComment={onAddNewComment} /> 
                : <Comment newsId={id} notLoginUser />
            }
            {comments.map(comment => (
              <Comment
                key={comment.id}
                newsId={id}
                comment={comment}
                onClickNewReply={() => {
                  onClickNewReply(comment.id);
                }}
              />
            ))}
          </div>
      }

    </Layout>
  );
}
