import React from "react";
import moment from "moment";
import QuillEditor from "../../../Common/Component/QuillEditor";
import "./index.scss";

import checkedIcon from "../../../Image/checkbox-checked.svg"
import uncheckedIcon from "../../../Image/checkbox-unchecked.svg"

function Input(props) {
  return (
    <Base {...props} type="input">
      <input 
        name={props.name}
        value={props.value}
        placeholder={props.placeholder}
        onChange={e => {
          props.onChange(props.name, e.currentTarget.value);
        }}
      />
    </Base>
  );
}

function TextArea(props) {
  return (
    <Base {...props} type="textarea">
      <textarea 
        name={props.name}
        value={props.value}
        placeholder={props.placeholder}
        maxLength={props.maxLength}
        onChange={e => {
          props.onChange(props.name, e.currentTarget.value);
        }}
      />
    </Base>
  );
}

function RichTextArea(props) {
  return (
    <Base {...props} type="rich-textarea">
      <QuillEditor
        name={props.name}
        placeholder={props.placeholder}
        value={props.value}
        onChange={html => {
          props.onChange(props.name, html);
        }}
      />
    </Base>
  );
}

function Phone(props) {
  return (
    <Base {...props} type="phone">
      <input
        type="text"
        name={props.name}
        value={props.value}
        placeholder={props.placeholder}
        pattern="[0-9]{0,5}"
        onChange={e => {
          const regex = /^[0-9\b]+$/;
          if (regex.test(e.target.value)) {
            props.onChange(props.name, e.currentTarget.value);
          }
        }}
      />
    </Base>
  );
}

class Date extends React.Component {

  get dateValue() {
    return this.props.value || moment();
  }
  get year() {
    return this.dateValue.year();
  }
  get month() {
    return this.dateValue.month();
  }
  get date() {
    return this.dateValue.date();
  }

  get yearList() {
    const currentYear = moment().year();
    return [...new Array(100)].map((_, index) => currentYear - index);
  }
  get monthList() {
    return [...new Array(12)].map((_, index) => index);
  }
  get dateList() {
    return [...Array(this.getEndDateOf(this.month))].map((_, index) => 1 + index);
  }
  getEndDateOf(month) {
    return moment().set("M", month).endOf("month").date();
  }

  onChangeYear = e => {
    const year = Number(e.currentTarget.value);
    this.onChange(year);
  }
  onChangeMonth = e => {
    const month = Number(e.currentTarget.value);
    const endDate = this.getEndDateOf(month);
    if(this.date > endDate) {
      this.onChange(undefined, month, endDate);
    } else {
      this.onChange(undefined, month);
    }
  }
  onChangeDate = e => {
    const date = Number(e.currentTarget.value);
    this.onChange(undefined, undefined, date);
  }
  onChange(year = this.year, month = this.month, date = this.date) {
    const value = moment(`${year}-${month + 1}-${date}`);
    this.props.onChange(this.props.name, value);
  }

  render() {
    return (
      <Base {...this.props} type="date">
        <select
          name="year"
          value={this.year}
          onChange={this.onChangeYear}
        >
          {this.yearList.map(value => (
            <option key={value} value={value}>{value}</option>
          ))}
        </select>
        <select
          name="month"
          value={this.month}
          onChange={this.onChangeMonth}
        >
          {this.monthList.map(value => (
            <option key={value} value={value}>{value + 1}</option>
          ))}
        </select>
        <select
          name="date"
          value={this.date}
          onChange={this.onChangeDate}
        >
          {this.dateList.map(value => (
            <option key={value} value={value}>{value}</option>
          ))}
        </select>
      </Base>
    );
  }
}

function CheckBox(props) {
  return (
    <Root {...props} type="checkbox">
      <input
        type="checkbox"
        id={props.name}
        name={props.name}
        checked={props.value}
        onChange={e => {
          props.onChange(props.name, e.currentTarget.checked);
        }}
        hidden
      />
      <label htmlFor={props.name}>
        <img
          src={props.value ? checkedIcon : uncheckedIcon}
          alt="checkbox"
        />
        <span className="challenge-apply__field__title">
          {props.title || "-"}
        </span>
      </label>
    </Root>
  );
}

function Base(props) {
  return (
    <Root {...props}>
      <div className="challenge-apply__field__header">
        <div className="challenge-apply__field__title">
          {props.title || "-"}
        </div>
      </div>
      <div className="challenge-apply__field__body">
        {props.children}
      </div>
    </Root>
  );
}

function Root(props) {
  return (
    <div className={
      "challenge-apply__field"
      + ` challenge-apply__field--${props.type}`
      + (props.classModifier ? ` challenge-apply__field--${props.classModifier}` : "")
    }>
      {props.children}
    </div>
  );
}

const Field = {
  Input,
  TextArea,
  RichTextArea,
  Phone,
  Date,
  CheckBox
};

export default Field;
