import React from "react";
import * as request from "../../../../Common/Util/HTTPRequest";
import View from "./View";

class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      games: [],
      showsMore: true
    }
  }
  componentDidMount() {
    this.loadMoreGames(15);
  }

  loadMoreGames(limit) {

    request
      .getProjectsByType({
        type: "news",
        offset: this.state.games.length,
        limit: limit,
      })
      .then(res => res.json())
      .then(games => {
        this.setState(prevState => ({ 
          games: [].concat(prevState.games, games)
        }));
      })
  }

  onClickMore = () => {
    this.setState(() => {
      this.loadMoreGames(15);
    })
  }

  onScroll = e => {
    const window = document.documentElement;
    if (
      !this.isLoading
      && window.scrollTop + window.clientHeight >= window.scrollHeight - 300
    ) {
      this.loadMoreGames();
    }
  }

  render() {
    return (
      <View
        {...this.state}
        onClickMore={this.onClickMore}
      />
    );
  }
}

export default Container;
