import React from "react";
import "./index.scss";

import oobcDefaultIcon from "../../../../Image/learn-tab-oobc-default-icon.png";
import oobcActiveIcon from "../../../../Image/learn-tab-oobc-active-icon.png";
import jsDefaultIcon from "../../../../Image/learn-tab-js-default-icon.png";
import jsActiveIcon from "../../../../Image/learn-tab-js-active-icon.png";

function Tabs(props) {
  return (
    <div className="learn__tabs">
      {props.tabs.map((tab, index) => {
        const selected = index === props.selectedTabIndex;
        return (
          <div
            key={index}
            className={
              "learn__tab"
              + (selected ? " learn__tab--selected" : "")
            }
            onClick={() => {
              props.onClickTabAt(index);
            }}
          >
            <img
              className="learn__tab__icon"
              src={selected ? 
                (tab.subtitle === "텍스트코딩" ? jsActiveIcon : oobcActiveIcon) : 
                (tab.subtitle === "텍스트코딩" ? jsDefaultIcon : oobcDefaultIcon)
              }
              alt="tab-icon"
            />
            <div className="learn__tab__title">{tab.title}</div>
          </div>
        )
      })}
    </div>
  )
}

export default Tabs;
