import React from "react";
import PublishedItem from "../../../../Common/Component/PublishedItem";
import PlusIcon from "../../../../Image/more-bttn-plus.svg";
import "./index.scss";

function View(props) {
  return (
    <div className="recent-games">
      <div className="recent-games__grid">
        {props.games.map(game => (
          <div key={game.pId} className="recent-game">
            <PublishedItem published={game} />
          </div>            
        ))}
      </div>
      {props.showsMore && (
        <button
          className="recent-games__more-btn"
          onClick={props.onClickMore}
        >
          <img className="recent-games__more-btn_plus" src={PlusIcon} alt="plus-icon"/>
          더보기
        </button>
      )}
    </div>

  );
}

export default View;
