import React from "react";
import { FormattedMessage } from "react-intl";
import PublishedItem from "../../../../Common/Component/PublishedItem";
import "./index.scss";

import searchIcon from "../../../../Image/icon-search.svg";

function View(props) {
  return (
    <div className="play__search-results">
      <div className="play__search-results__header">
        <div className="play__search-results__title">
          <FormattedMessage id="ID_SEARCH_RESULT" />
        </div>
        <div className="play__search-results__keyword">
          {`${props.keyword}(${props.total})`}
        </div>
      </div>

      <Results total={props.total} results={props.results} />
    </div>
  )
}

export default View;

function Results(props) {
  return (
    <div className="play__search-result">
      {props.results.length > 0 ? (
        <div className="play__search-result__items">
          {props.results.map(result => (
            <div key={result.pId} className="play__search-result__item">
              <PublishedItem published={result} />
            </div>            
          ))}
        </div>
      ) : (
        <div className="play__search-result__placeholder">
          <img
            className="play__search-result__placeholder__icon"
            src={searchIcon}
            alt="search"
          />
          <FormattedMessage id="ID_GAME_SEARCH_RESULT_PLACEHOLDER" />
        </div>
      )}
    </div>
  )
}
