import React, { useEffect, useState } from "react";
import styled from "@emotion/styled";
import { Global, css } from "@emotion/react";
import Contents from "./Components/Contents";
import arrowPrev from "../../Image/dreamclass/ms-slider-left-arrow.png";
import arrowNext from "../../Image/dreamclass/ms-slider-right-arrow.png";
import { getPopularLectures } from "./api";

import "./index.scss";
import { Lecture } from "../../models";

const GlobalStyle = css`
  *:focus {
    outline:none;
  }

  .slick-list {
    font-size: 0;
  }

  .slick-track {
    min-width: 1200px;
  }

  .slick-slide {
    position: relative;

    &:not(.slick-active) {
      &:before {
        content: "";
        position: absolute;
        left: 15px;
        top: 0;
        background-color: rgba(0, 0, 0, 0.75);
        width: calc(100% - 30px);
        height: 100%;
        border-radius: 16px;
        z-index: 10;
        @media screen and (max-width: 1169px) {
          background-color: transparent;
        }
      }
    }
  }

  .slick-contents {
    .slick-slide:not(.slick-active):before {
      height: 370px;
    }
  }

  .slick-arrow {
    font-size: 0;
    border: none;
    padding: 0;
    width: 26px !important;
    height: 46px !important;
    background-color: transparent;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    margin-top: -26px;
    z-index: 100;
    cursor: pointer;

    &:before {
      display: none;
    }


    &.slick-prev {
      left: -40px;
      background: url(${arrowPrev}) no-repeat center/cover;
      &:hover {
        background: url(${arrowPrev}) no-repeat center/cover;
      }
      @media (max-width: 1300px) {
        left: 15px;
      }
    }

    &.slick-next {
      right: -40px;
      background: url(${arrowNext}) no-repeat center/cover;
      &:hover {
        background: url(${arrowNext}) no-repeat center/cover;
      }
      @media (max-width: 1300px) {
        right: 15px;
      }
    }

    &:focus {
      outline: none;
    }
  }

  .slick-dots {
    list-style: none;
    padding: 0;
    margin: 14px 0 18px;
    display: flex !important;
    justify-content: center;
    position: relative;
    bottom: 0;

    li {
      margin: 0 5px;
      font-size: 0;
      width: 8px !important;
      height: 8px !important;

      button {
        font-size: 0;
        width: 8px !important;
        height: 8px !important;
        background-color: rgba(255, 255, 255, 0.5);
        border-radius: 4px;
        border: none;
        padding: 0;
        cursor: pointer;

        &:before {
          display: none;
        }
      }

      &.slick-active {
        button {
          background-color: #fff;
        }
      }

      &:not(.slick-active) {
        button {
          background-color: rgba(255, 255, 255, 0.5);
        }
      }
    }
  }
`;

const Self = styled.div`
  overflow: hidden;
  background-color: #F4F6F8;
  height: 100vh;
  display: flex;
  align-items: center;
`;

const View = (props) => {
  const [popularLectures, setPopularLectures] = useState([]);

  useEffect(() => {
    getPopularLectures().then((lectures) => {
      // tmp remove AstroBoy from list - id = 49
      let newLectures = lectures.filter((lecture) => (lecture.id !== 49))
      setPopularLectures(newLectures.map((l) => new Lecture(l)));
    });
  }, []);

  return (
    <Self>
      <Global styles={GlobalStyle} />
      <Contents items={popularLectures} />
    </Self>
  );
};

export default View;
