import React, { useState } from "react";
import "./index.scss"

import clearImg from "../../../../../../Image/dream_questClear.svg"

function CertificateInput(props) {
  const [name, setName] = useState(props.defaultName || "");
  const [organization, setOrganization] = useState(props.defaultOrganization || "");
  return  (
    <div
      className={
        `dream_certInput ${props.withProjectClear ? "dream_certInput-projectClear" : ""}`
      }
    >
      {props.withProjectClear ? (
        <>
          <img className="dream_certInput_image" src={clearImg} alt="clear"/>
          <p className="popup_title">QUEST CLEAR</p>
          <div className="popup_subtitle">
            {`축하합니다! 모든 미션에 성공했어요.\n다음 항목들을 입력하고 인증서를 출력하세요!`}
          </div>
        </>
      ) : (
        <p className="popup_title">인증서 정보 입력</p>
      )}
      <div className="popup_fields">
        <div className="popup_field">
          <div className="popup_field_title">
            이름
          </div>
          <input
            className="popup_field_input"
            placeholder="이름을 입력해주세요"
            value={name}
            onChange={e => {
              setName(e.currentTarget.value);
            }}
          />
        </div>
        <div className="popup_field">
          <div className="popup_field_title">
            소속
          </div>
          <input
            className="popup_field_input"
            placeholder="소속을 입력해주세요"
            value={organization}
            onChange={e => {
              setOrganization(e.currentTarget.value);
            }}
          />
        </div>  
      </div>
      <div className="popup_buttons">
        <button
          className="popup_button popup_button-cancel"
          onClick={() => {
            if (props.onClickCancel) props.onClickCancel();
            if (props.dismiss) props.dismiss();
          }}
        >
          나중에 하기
        </button>
        <button
          className="popup_button"
          disabled={!name || !organization}
          onClick={() => {
            if (props.onClickConfirm) props.onClickConfirm(name, organization);
            if (props.dismiss) props.dismiss();
          }}
        >
          인증서 출력하기
        </button>
      </div>
    </div>
  );
}

export default CertificateInput;
