import React, { Component } from "react";
import { connect } from "react-redux";
import { injectIntl } from "react-intl";
import * as request from "../../../Common/Util/HTTPRequest";
import PopUp, { showPopUp } from "../../../Common/Component/PopUp";
import View from "./View";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      challengeItems : []
    };
  }

  componentDidMount() {
    request.getMyCreatorChallenges({userId: this.props.userId, season: "2"})
      .then(res=> res.json())
      .then(json=> this.setState({challengeItems: json}))
      .catch(e=> console.error(e));
  }

  onClickItem = (e, id) => {
    e.stopPropagation();
    this.props.history.push(`/challenge/project/${id}`);
  }

  onClickEdit = (e, id) => {
    e.stopPropagation();
    this.props.history.push(`/challenge/apply/${id}`);
  }

  onClickRemove = (e, id) => {
    e.stopPropagation();
    const formatMessage = this.props.intl.formatMessage;
    showPopUp(
      <PopUp.TwoButton
        title={formatMessage({id: "ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETE_CONFIRM"})}
        confirmButtonName={formatMessage({id: "ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETE_YES"})}
        cancelButtonName={formatMessage({id: "ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETE_NO"})}
        confirmAction={()=>{
          request.deleteCreatorChallenge({id})
          .then(()=>{
            showPopUp(
              <PopUp.OneButton
                title={formatMessage({ id: "ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETED" })}
                buttonName={formatMessage({ id: "ID_CHALLENGE_DETAIL_PROJECT_LIST_ITEM_DELETE_YES" })}
              />
            );
            this.setState({
              challengeItems: this.state.challengeItems.filter(item=> item.id !== id)
            })
          })
          .catch(e=> console.error(e));
        }}    
      />
    );
  }

  render() {
    const { challengeItems } = this.state;
    const { onClickRemove, onClickItem, onClickEdit } = this;
    return (
      <View 
        challengeItems={challengeItems} 
        onClickRemove={onClickRemove} 
        onClickItem={onClickItem} 
        onClickEdit={onClickEdit}
      />
    )
  }
}

export default connect(
  state => ({ userId: state.userinfo.id }),
  {}
)((injectIntl(Container)));
