import React from "react";
import { injectIntl } from "react-intl";

import userRankingTopBg from "../../../../Image/userRankingTopBg.svg";
import userRankingTopCircle from "../../../../Image/userRankingTopCircle.png";
// import userRankingTopBlankIcon from "../../../../Image/userRankingTopBlankIcon.svg";
import userRankingOtherBg from "../../../../Image/userRankingOtherBg.svg";
import userRankingGoldCrown from "../../../../Image/gold-crown.svg";
import userRankingSilverCrown from "../../../../Image/silver-crown.svg";
import userRankingBronzeCrown from "../../../../Image/bronze-crown.svg";
import userRankingOtherCircle from "../../../../Image/userRankingOtherCircle.png";
// import userRankingOtherBlankIcon from "../../../../Image/userRankingOtherBlankIcon.svg";
import guestProfileImage from "../../../../Image/guest-profile.svg";

import "./index.scss";

const UserRanking = props => {
  const { ranking } = props;
  const { formatMessage, formatNumber } = props.intl;
  return (
    <div className="UserRanking">
      {ranking.rank <= 3 ? (
        <div className={`RankingTop ${ranking.rank !== 3 ? "TopSpacing" : ""}`}>
          <div className="RankingTopCrown">
            {ranking.rank === 1 && (
              <img src={userRankingGoldCrown} alt="gold rank" />
            )}
            {ranking.rank === 2 && (
              <img src={userRankingSilverCrown} alt="silver rank" />
            )}
            {ranking.rank === 3 && (
              <img src={userRankingBronzeCrown} alt="bronze rank" />
            )}
          </div>
          <div className="RankingTopContainer">
            <img
              className="RankingTopBg"
              src={userRankingTopBg}
              alt="top rank bg"
            />
            <div className="RankingTopContent">
              <div className="UserIcon">
                <img
                  className="RankingContentEdge"
                  src={userRankingTopCircle}
                  alt="rank edge"
                />
                <img
                  className="Icon"
                  src={ranking.user ? ranking.user.icon : guestProfileImage}
                  alt="user"
                />
              </div>
              <div className="UserInfo">
                <p className="UserName">
                  {ranking.user ? ranking.user.name : ranking.guestId}
                </p>
                <p className="UserScore">
                  {formatMessage(
                    { id: "ID_RANKING_SCORE" },
                    { score: formatNumber(ranking.score) }
                  )}
                </p>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div
          className={`RankingOther ${
            ranking.rank !== 10 ? "OtherSpacing" : ""
          }`}
        >
          <div className="RankingLabel">{ranking.rank}</div>
          <div className="RankingOtherContainer">
            <img
              className="RankingOtherBg"
              src={userRankingOtherBg}
              alt="rank bg"
            />
            <div className="RankingOtherContent">
              <div className="UserIcon">
                <img
                  className="RankingContentEdge"
                  src={userRankingOtherCircle}
                  alt="rank edge"
                />
                <img
                  className="Icon"
                  src={ranking.user ? ranking.user.icon : guestProfileImage}
                  alt="user"
                />
              </div>
              <div className="UserInfo">
                <p className="UserName">
                  {ranking.user ? ranking.user.name : ranking.guestId}
                </p>
                <p className="UserScore">
                  {formatMessage(
                    { id: "ID_RANKING_SCORE" },
                    { score: formatNumber(ranking.score) }
                  )}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default injectIntl(UserRanking);
