import React, { Component } from "react";
import "./index.scss";
import { injectIntl } from "react-intl";
import Field from "../../../../Common/Component/Field";
import Validate from "../../../../Common/Util/Validate";

class AccountEmailPopup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: ""
    };
  }

  onChangeField = (id, value) => { 
    this.setState({ [id]: value }) 
  }

  onClickSubmit = () => {

  }

  render() {
    const { intl } = this.props
    const { email } = this.state

    const validateEmail = Validate.email(email)
    const warningMsg = email && validateEmail.id && intl.formatMessage({ id: `${validateEmail.id}` })
    return (
      <div className="account__manage__email-popup">
        <div className="account__manage__email-popup__title">
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_EMAIL_POPUP_TITLE"})}
        </div>
        <div className="account__manage__email-popup__msg">
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_EMAIL_POPUP_MSG"})}
        </div>

        <Field.Text
          id={"email"}
          value={email}
          warning={warningMsg}
          placeholder={intl.formatMessage({ id: "ID_VALIDATE_FAIL_EMAIL_NULL"})}
          onChange={this.onChangeField}
        />

        <button disabled={!validateEmail.result} onClick={this.onClickSubmit}>
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_EMAIL_SUBMIT"})}
        </button>
      </div>
    );
  }
}

export default injectIntl(AccountEmailPopup);
