import React from "react";
import { FormattedMessage } from "react-intl";
import Field from "../../Common/Component/Field";
import Layout from "../../Common/Component/Layout";
import "./index.scss";

const View = props => {
  const {
    email,
    emailWarningId,
    isOverAge14,
    isRegistered,
    onChange,
    onClickPrevPage,
    onSubmit,
    onClickBack
  } = props;

  return (
    <Layout>
      <div className="email-verify-page">
        {isRegistered
          ? <EmailConfirm
              email={email}
              onClickBack={onClickBack}
              onSubmit={onSubmit}
            />
          : <EmailRegister
              email={email}
              emailWarningId={emailWarningId}
              isOverAge14={isOverAge14}
              onChange={onChange}
              onSubmit={onSubmit}
              onClickPrevPage={onClickPrevPage}
            />
          }
      </div>
    </Layout>
  )
}

export default View;

const EmailRegister = props => {
  const { email, emailWarningId, isOverAge14, onChange, onSubmit, onClickPrevPage } = props;
  return (
    <React.Fragment>
      <div className="email-verify-page__title">
        {isOverAge14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_TITLE1" />
          : <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_TITLE2" />
        }
      </div>
      <p className="email-verify-page__message">
        {isOverAge14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_DESC1" />
          : <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_DESC2" />
        }
      </p>
      <Field.Text
        id="email"
        value={email}
        onChange={onChange}
        warning={email && emailWarningId && <FormattedMessage id={emailWarningId} />}
        placeholder="Ex) wizlab@wizschool.io"
      />
      <button className="email-verify-page__button" onClick={onSubmit} disabled={!!emailWarningId}>
        <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_BTN" />
      </button>
      <div className="email-verify-page__cancel" onClick={onClickPrevPage}>
        <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_CANCEL" />
      </div>
    </React.Fragment>
  )
}

const EmailConfirm = props => {
  const { email, onSubmit, onClickBack } = props;
  return (
    <React.Fragment>
      <div className="email-verify-page__title">
        <FormattedMessage id="ID_EMAIL_VERIFY_CONFIRM_TITLE" />
      </div>
      <p className="email-verify-page__message">
        <FormattedMessage id="ID_EMAIL_VERIFY_CONFIRM_DESC" values={{ email }} />
      </p>
      <div className="email-verify-page__buttons">
        <button onClick={onClickBack}>
          <FormattedMessage id="ID_EMAIL_VERIFY_CONFIRM_BTN2" />
        </button>
        <button onClick={onSubmit}>
          <FormattedMessage id="ID_EMAIL_VERIFY_CONFIRM_BTN1" />
        </button>
      </div>
    </React.Fragment>
  )
}
