import React from "react";
import * as request from "../../../../Common/Util/HTTPRequest";
import View from "./View";

class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: "likeCount",
      creators: []
    }
  }
  componentDidMount() {
    this.loadCreators();
  }
  loadCreators = () => {
    request
      .getRankingByType({
        type: this.state.selectedTab,
        limit: 10
      })
      .then(res => res.json())
      .then(creators => {
        this.setState({ creators });
      })
  }

  onClickTab = tab => {
    this.setState({ selectedTab: tab },
      this.loadCreators
    );
  }

  render() {
    return (
      <View
        {...this.state}
        onClickTab={this.onClickTab}
      />
    );
  }
}

export default Container;
