import React from "react";
import { Link } from "react-router-dom";
import moment from "moment";
import { injectIntl, FormattedMessage } from "react-intl";
import Game from "./Component/Game";
import Comment from "../Comment";
import "./index.scss";
import likeOnImg from "../../../Image/like-on.svg";
// import likeOffImg from "../../../Image/like-off.svg";
import likeImg from "../../../Image/icon-like.svg";
import replyIconImg from "../../../Image/icon-comment-white.svg";
import shareIconImg from "../../../Image/icon-share-w.svg";
import editIconImg from "../../../Image/edit.svg";
// import arrowDownImg from "../../../Image/social_arrow-down.svg";
// import copyProjectIconImg from "../../../Image/copy-project.svg";
import sirenIconImg from "../../../Image/siren.svg";
import officialIcon from "../../../Image/certification-mark.svg";

function View(props) {
  const {
    userinfo,
    project,
    handleLikeToggle,
    handleShareProject,
    handleCopyProject,
    handleEditProject,
    isLiked,
    isSubscribed,
    subscribeCount,
    handleClose,
    moveToComment,
    handleSubscribe,
    commentRef,
    handleReport,
    randomGames,
    isDescFold,
    handleFold,
    isMobile,
    handleProjectCommentCount,
    allCommentCount,
    handleRandomGame
  } = props;
  const isMine = userinfo.email && userinfo.email === project.user.email;
  const screenMode = project.screenMode ? project.screenMode : "HORIZONTAL";

  return (
    <div id="wizappDetail">
      <div className="header__close header__close--pc" onClick={handleClose} />
      <div className="header__project--buttons pc">
        <div className="likeButton" onClick={handleLikeToggle}>
          <img src={isLiked ? likeOnImg : likeImg} alt="like" />
          <label className="CountLabel">{project.likeCount}</label>
        </div>
        <div className="replyButton" onClick={moveToComment}>
          <img src={replyIconImg} alt="reply" />
          <label className="CountLabel">{allCommentCount}</label>
        </div>
        <div className="shareButton" onClick={handleShareProject}>
          <img src={shareIconImg} alt="share" />
        </div>
        {!isMine && (
          <div className="sirenButton" onClick={handleReport}>
            <img src={sirenIconImg} alt="siren" />
          </div>
        )}

        {isMine && (
          <div className="EditButton button" onClick={handleEditProject}>
            <img src={editIconImg} alt="edit" />
          </div>
        )}
      </div>
      <div className="wizappDetail__inner">
        <div className="wizappDetail__header">
          <div
            className="header__close header__close--mobile"
            onClick={handleClose}
          />
          <div className="header__project--thumbnail">
            <img src={project.icon} alt="thumbnail" />
          </div>
          <div className="header__project--info">
            <p className="info__title">{project.name}</p>
            <p className="info__sub">
              <span className="viewCount">{project.viewCount}</span>
            </p>
          </div>
          {(project.isCopyAllowed ||
            userinfo.email === "gstar-admin@wizschool.io" ||
            userinfo.email === "chris@wizschool.io" ||
            userinfo.email === "k@wizschool.io" ||
            userinfo.email === "ice@wizschool.io" ||
            userinfo.email === "rei@wizschool.io" ||
            userinfo.email === "wizedu@wizschool.io" ||
            userinfo.email === "zoe@wizschool.io") && (
            <div className="game__copyButton" onClick={handleCopyProject}>
              <FormattedMessage id="ID_WIZAPPDETAIL_COPY_CODE" />
            </div>
          )}
        </div>
        <div className="wizappDetail__content">
          <div className="content__game">
            <Game project={project} pId={project.pId} screenMode={screenMode} />
          </div>
          <div className="header__project--buttons mobile">
            <div className="likeButton" onClick={handleLikeToggle}>
              <img src={isLiked ? likeOnImg : likeImg} alt="like" />
              <label className="CountLabel">{project.likeCount}</label>
            </div>
            <div className="replyButton" onClick={moveToComment}>
              <img src={replyIconImg} alt="reply" />
              <label className="CountLabel">{allCommentCount}</label>
            </div>
            <div className="shareButton" onClick={handleShareProject}>
              <img src={shareIconImg} alt="share" />
            </div>
            {!isMine && (
              <div className="sirenButton" onClick={handleReport}>
                <img src={sirenIconImg} alt="siren" />
              </div>
            )}

            {isMine && (
              <div className="EditButton button" onClick={handleEditProject}>
                <img src={editIconImg} alt="edit" />
              </div>
            )}
          </div>
          <div className={`content__description`}>
            <div className={`description--${isDescFold ? "fold" : "full"}`}>
              {project.description}
            </div>
            {isDescFold && (
              <div className="description--more" onClick={() => handleFold()}>
                <FormattedMessage id="ID_WIZAPPDETAIL_VIEW_MORE" />
              </div>
            )}
            <p className="info__sub__date">
              <span className="createDate">
                <FormattedMessage id="ID_WIZAPPDETAIL_CREATE_DATE" />{" "}
                {moment(project.createdAt).format("YYYY.MM.DD")}
              </span>
              <span className="editDate">
                <FormattedMessage id="ID_WIZAPPDETAIL_EDIT_DATE" />{" "}
                {moment(project.editedAt).format("YYYY.MM.DD")}
              </span>
            </p>
          </div>

          <div className="content__user">
            <Link
              to={`/userpage/${project.user.id}`}
              style={{ textDecoration: "none" }}
            >
              <img className="user__img" src={project.user.icon} alt="" />
              <p className="user__name">
                {project.user.name}
                {userinfo.isOfficial && (
                  <img
                    className="user__officialImg"
                    src={officialIcon}
                    alt={userinfo.name}
                  />
                )}
              </p>
            </Link>
            {/* <p className="user__organization">{project.user.organization}</p> */}
            <p className="user__subscribe">
              <FormattedMessage id="ID_WIZAPPDETAIL_FOLLOWER" />{" "}
              {subscribeCount}
              <FormattedMessage id="ID_WIZAPPDETAIL_FOLLOWER_COUNT" />
            </p>
            <p className="user__statusMessage">{project.user.statusMessage}</p>
            {!isMine && (
              <button
                className={`UserSubscribe UserInfo__subscribe ${isSubscribed &&
                  "subscribe"}`}
                onClick={() => handleSubscribe(project.user.email)}
              >
                {isSubscribed ? (
                  <FormattedMessage id="ID_WIZAPPDETAIL_FOLLOWING" />
                ) : (
                  <FormattedMessage id="ID_WIZAPPDETAIL_DO_FOLLOW" />
                )}
              </button>
            )}
          </div>
        </div>
        <div className="wizappDetail__bottom">
          <p className="wizappDetail__bottom--comment-count__pc">
            <FormattedMessage id="ID_WIZAPPDETAIL_COMMENT" /> {allCommentCount}
          </p>
          {isMobile ? (
            <div className="wizappDetail__bottom--wrapper">
              <div className="wizappDetail__bottom--games">
                {randomGames.slice(0, isMobile ? 6 : 2).map((item, index) => {
                  return (
                    <div className="gameItem" key={index}>
                      <div
                        className="gameItem__projectimg-wrapper"
                        onClick={() => {
                          handleRandomGame(item.pId);
                        }}
                      >
                        <img
                          className="gameItem__projectimg"
                          src={item.icon}
                          alt=""
                        />
                      </div>
                      <div className="gameItem__projectname">
                        <span className="gameItem__projectname__text">
                          {item.name}
                        </span>
                      </div>

                      <Link
                        to={`/userpage/${item.user.id}`}
                        style={{ textDecoration: "none" }}
                      >
                        <div className="gameItem__username">
                          {item.user.name}
                        </div>
                      </Link>
                    </div>
                  );
                })}
              </div>
              <div className="wizappDetail__bottom--comment" ref={commentRef}>
                <Comment
                  parentType="published"
                  parentId={project.pId}
                  allCommentCount={allCommentCount}
                  isMobile={isMobile}
                  handleProjectCommentCount={handleProjectCommentCount}
                />
              </div>
            </div>
          ) : (
            <div className="wizappDetail__bottom--wrapper">
              <div className="wizappDetail__bottom--comment" ref={commentRef}>
                <Comment
                  parentType="published"
                  parentId={project.pId}
                  allCommentCount={allCommentCount}
                  isMobile={isMobile}
                  handleProjectCommentCount={handleProjectCommentCount}
                />
              </div>
              <div className="wizappDetail__bottom--games">
                {randomGames.slice(0, isMobile ? 6 : 2).map((item, index) => {
                  return (
                    <div className="gameItem" key={index}>
                      <div
                        className="gameItem__projectimg-wrapper"
                        onClick={() => {
                          handleRandomGame(item.pId);
                        }}
                      >
                        <img
                          className="gameItem__projectimg"
                          src={item.icon}
                          alt=""
                        />
                      </div>
                      <div className="gameItem__projectname">
                        <span className="gameItem__projectname__text">
                          {item.name}
                        </span>
                      </div>

                      <Link
                        to={`/userpage/${item.user.id}`}
                        style={{ textDecoration: "none" }}
                      >
                        <div className="gameItem__username">
                          {item.user.name}
                        </div>
                      </Link>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
export default injectIntl(View);
