import React from "react";
import "./index.scss";

export default class Animation extends React.Component {
  constructor(props) {
    super(props);
    this.state = { animationIndex: -1 };
    this.timer = undefined;
  }

  componentWillUnmount(){
    if(this.timer) clearInterval(this.timer);
  }

  start(loop) {
    if(this.timer) clearInterval(this.timer);

    const { images, frameRate } = this.props;
    this.setState({ animationIndex: 0 },()=>{
      this.timer = setInterval(()=>{
        let nextIndex = this.state.animationIndex + 1;
        if(nextIndex === images.length) {
          nextIndex = 0;
        }

        if(!loop && nextIndex === 0) {
          this.stop();
        } else {
          this.setState({ animationIndex: nextIndex });
        }
      },1000 / frameRate);
    });
  }

  stop() {
    if(this.timer) clearInterval(this.timer);
    this.setState({ animationIndex: -1 });
    if(this.props.onStop) this.props.onStop();
  }

  render() {
    const { images } = this.props;
    const { animationIndex } = this.state;
    const image = images[animationIndex];

    return (
      <div className="custom-sprite__editor__preview__animation-container__animation">
        {image && <img src={image} alt="preview img" />}
      </div>
    );
  }
}
