import React, { Component } from "react";
import { connect } from "react-redux";
import { injectIntl } from "react-intl";
import View from "./View";
// import * as TrackingUtil from "../../Common/Util/TrackingUtil";

import banner0 from "../../Image/event0banner.jpg";
import banner1 from "../../Image/event1banner.png";
import banner3 from "../../Image/event3_thumb.png";
import banner_challenge from "../../Image/event_challenge_banner.png";
import banner_camp from "../../Image/ocp-banner-camp@2x.png";
import banner_review from "../../Image/event__review.png";
import banner_reviewResult from "../../Image/event_reviewResult.png";
import signupBanner from "../../Image/signup-banner.png";
import signupEndBanner from "../../Image/signupEnd-banner.png";
import banner5 from "../../Image/event5_thumb.png";
import banner6 from "../../Image/event6_thumb.png";
import banner7 from "../../Image/event7_thumb.png";
import ev2result from "../../Image/ev2_result.jpg";
import challengeResult from "../../Image/challenge-event-thumb.png";
import ocp2banner from "../../Image/evtOcpBanner.png";
import ocp2_event_banner_2021 from "../../Image/ocp2_event_banner_2021.png";
import ocpbanner_2021 from "../../Image/pc-wizlab-2021-ocp-event@2x.png";
import creater_challenge from "../../Image/pc-wizlab-create-challenge-event@2x.png";
import challenge2Result from "../../Image/challenge2-event-end.png";
import gstarBanner from "../../Image/pc_GSTAR_event@2x.png";

class Container extends Component {
  constructor(props) {
    super(props);

    this.TAB_TYPE = {
      ING: props.intl.formatMessage({ id: "ID_EVENT_LIVE" }),
      ANNOUNCE: props.intl.formatMessage({ id: "ID_EVENT_RESULT" })
    };
    this.events = [
      {
        title: "2021 Wizlab X G-STAR 크리에이터 챌린지",
        path: "/challenge",
        coverImg: gstarBanner,
        startDate: "2021.10.15",
        expiredDate: "11/05(종료)"
      },
      {
        title: "2021 온라인코딩파티 시즌2",
        path: "/learn",
        coverImg: ocp2_event_banner_2021,
        startDate: "2020.10.19",
        expiredDate: "11/21(종료)"
      },
      {
        title: "2021 온라인코딩파티 시즌 1",
        path: "/learn",
        coverImg: ocpbanner_2021,
        startDate: "2021.06.14",
        expiredDate: "7.25(종료)"
      },
      {
        title: "2021 위즈랩 크리에이터 챌린지",
        path: "/challenge2021",
        coverImg: creater_challenge,
        startDate: "2021.06.15",
        expiredDate: "7.30(종료)"
      },
      {
        title: "온라인코딩파티 시즌2",
        path: "/learn",
        coverImg: ocp2banner,
        startDate: "2020.10.19",
        expiredDate: "11.01(종료)"
      },
      {
        title: "회원가입하면 선물 팡팡 ",
        path: "/event/codingparty/signupEnd",
        coverImg: signupBanner,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "미션 후기 이벤트",
        path: "/event/codingparty/reviewEnd",
        coverImg: banner_review,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "위즈랩 게임 크리에이터 챌린지",
        path: "/event/codingparty/challengeEnd",
        coverImg: banner_challenge,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "위즈랩 온라인 캠프",
        path: "/event/codingparty/onlinecamp",
        coverImg: banner_camp,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      }
    ];
    this.results = [
      {
        title: "위즈랩 게임 크리에이터 챌린지",
        path: "/event/codingparty/challenge2End",
        coverImg: challenge2Result,
        startDate: "2021.06.15",
        expiredDate: "7.30(종료)"
      },
      {
        title: "미션 후기 이벤트",
        path: "/event/codingparty/reviewEnd",
        coverImg: banner_reviewResult,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "위즈랩 게임 크리에이터 챌린지",
        path: "/event/codingparty/challengeEnd",
        coverImg: challengeResult,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "회원가입하면 선물 팡팡 ",
        path: "/event/codingparty/signupEnd",
        coverImg: signupEndBanner,
        startDate: "2020.10.19",
        expiredDate: "11.29(종료)"
      },
      {
        title: "빙고를 완성하면 선물이 팡팡",
        path: "/event/end/7",
        coverImg: banner7,
        startDate: "2020.06.22",
        expiredDate: "08.02(종료)"
      },
      {
        title: "온라인 코딩파티",
        path: "/event/end/6",
        coverImg: banner6,
        startDate: "2020.06.22",
        expiredDate: "08.02(종료)"
      },
      {
        title: "회원가입 이벤트",
        path: "/event/end/5",
        coverImg: banner5,
        startDate: "2020.06.22",
        expiredDate: "07.19"
      },
      {
        title: "위즈랩 추천왕 이벤트",
        path: "/event/end/2",
        coverImg: ev2result,
        startDate: "2020.05.21",
        expiredDate: "06.08"
      },
      {
        title: "위즈랩 방탈출 게임만들고, 100% 선물받자!",
        path: "/event/end/1",
        coverImg: banner1,
        startDate: "2020.05.21",
        expiredDate: "06.08"
      },
      {
        title: "매직스펠 당첨자 발표!",
        path: "/event/end/0",
        coverImg: banner0,
        startDate: "2020.05.01",
        expiredDate: "05.31"
      },
      {
        title: "온라인 코딩파티",
        path: "/event/end/6",
        coverImg: banner3,
        startDate: "2020.06.22",
        expiredDate: "08.02(종료)"
      },
      {
        title: "빙고를 완성하면 선물이 팡팡",
        path: "/event/end/7",
        coverImg: banner7,
        startDate: "2020.06.22",
        expiredDate: "08.02(종료)"
      }
    ];

    this.state = {
      selectedTab: this.TAB_TYPE.ING
    };
  }

  // componentDidMount() {
  //   TrackingUtil.sendPageEvent("/event", this.props.email);
  // }

  handleSelectTab = tab => {
    if (tab === this.state.selectedTab) return;
    this.setState({ selectedTab: tab });
  };

  render() {
    const { events, results } = this;
    const { selectedTab } = this.state;
    const { intl } = this.props;
    return (
      <View
        selectedTab={selectedTab}
        TAB_TYPE={this.TAB_TYPE}
        handleSelectTab={this.handleSelectTab}
        events={events}
        results={results}
        intl={intl}
      />
    );
  }
}
export default connect(
  state => ({
    email: state.userinfo.email
  }),
  {}
)(injectIntl(Container));
