import React from 'react';
import { FormattedMessage } from "react-intl";
import alertIcon from "../../../Image/icon_warning@2x.png";
import closeIcon from "../../../Image/popup_close@2x.png";
import "./index.scss";

export default function index(props) {
  return (
    <div className="alert-ie-popup">
      <img className="alert-ie-popup__close-btn" src={closeIcon} alt="close popup" onClick={props.dismiss}/>
      <img className="alert-ie-popup__icon" src={alertIcon} alt="alert Icon" />
      <p className="alert-ie-popup__title">
        <FormattedMessage id="ID_LEARN_DETAIL_MOBILE_POPUP_MESSAGE01" />
      </p>
      <p className="alert-ie-popup__subtitle">
        <FormattedMessage id="ID_LEARN_DETAIL_MOBILE_POPUP_MESSAGE02" />
      </p>
      <button onClick={props.dismiss} >
        <FormattedMessage id="ID_LEARN_DETAIL_MOBILE_POPUP_CONFIRM_BUTTON"/>
      </button>
    </div>
  )
}
