import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { injectIntl } from "react-intl";
import { showPopUp } from "../../Component/PopUp";
import FindPw from "../../Component/FindPw";
import * as request from "../../Util/HTTPRequest";
import "./index.scss";

class FindLoginIdResult extends Component {
  constructor(props) {
    super(props);
    this.state = { reSend : false }
  }

  onClickResend = () => {
    request.sendEmailFindId({ verifiedEmail: this.props.verifiedEmail })
    this.setState({reSend : true})
  }

  onClickFindPwd = () => {
    showPopUp(
      <FindPw />,
      {
        darkmode: true,
        dismissButton: false,
        dismissOverlay: true
      }
    );
  }

  render() {
    const { formatMessage } = this.props.intl;

    return (
      <div className="findlogin-id-result">
        <div className="findlogin-id-result__title">
          {formatMessage({ id: "ID_EMAIL_AUTH_CONFIRM_TITLE" })}
        </div> 
        <div className="findlogin-id-result__desc"> 
          <FormattedMessage id="ID_FIND_LOGIN_ID_RESULT_DESC" values={{verifiedEmail: this.props.verifiedEmail}} />
        </div>
        <button className="findlogin-id-result_button" onClick={this.onClickResend}>
          {formatMessage({ id: "ID_FIND_LOGIN_ID_RETRY" })}
        </button>
        {this.state.reSend && <div className="findlogin-id-result__retry-desc">{formatMessage({ id: "ID_FIND_LOGIN_ID_RETRY_DESC" })}</div>}
        <div className="findlogin-id-result__desc__bottom_wrapper">
          <div className="findlogin-id-result__desc__bottom">
            {formatMessage({ id: "ID_FIND_LOGIN_ID_RESULT_DESC_BOTTOM" })}
          </div>
          <div className="findlogin-id-result__desc__bottom-link" onClick={this.onClickFindPwd}>
            {formatMessage({ id: "ID_FORGOTPW" })}
          </div>
        </div>
      </div>
    )
  }
}

export default injectIntl(FindLoginIdResult);
