import React from "react";
import { Link } from "react-router-dom";
import { FormattedMessage } from "react-intl"
import { withSubscribe } from "../../../../Common/Util/Subscribe";
import "./index.scss";

function CreatorRank(props) {
  const { creator, rank, tab } = props;
  const isSubscrbing = props.subscribeInfo.emails.includes(creator.email);
  return (
    <Link
      className="creator-rank"
      to={`/userpage/${creator.id}`}
    >
      <div className="creator-rank__rank">
        {rank}
      </div>
      <img 
        className="creator-rank__icon"
        src={creator.icon}
        alt="creato-icon"
      />
      <div className="creator-rank__section">
        <div className="creator-rank__name">
          {creator.name}
        </div>
        <div className="creator-rank__count">
          <FormattedMessage
            id={"ID_HOME_RANK_" + tab.toUpperCase()}
            values={{ count: creator[tab] }}
          />
        </div>
      </div>
      <button
        className={
          "creator-rank__subscribe-btn"
          + (isSubscrbing ? " creator-rank__subscribe-btn--active" : "")
        }
        onClick={e => {
          e.preventDefault();
          if(isSubscrbing) {
            props.unsubscribe(creator.email);
          } else {
            props.subscribe(creator.email);
          }
        }}
      >
        {isSubscrbing
          ? <FormattedMessage id="ID_HOME_RANK_CREATOR_FOLLOWING_BUTTON" />
          : <FormattedMessage id="ID_HOME_RANK_CREATOR_FOLLOW_BUTTON" />
        }
      </button>
    </Link>
  )
}

export default withSubscribe(CreatorRank);
