import React from "react";
import { Link } from "react-router-dom";
import { injectIntl, FormattedMessage } from "react-intl";
import Layout from "../../Common/Component/Layout";
import EditorPicks from "../../Common/Component/EditorPicks";
import SearchBar from "./Component/SearchBar";
import SearchResult from "./Component/SearchResult";
import Recommendations from "./Component/Recommendations"
import WeeklyTops from "./Component/WeeklyTops"
import NowPlayings from "./Component/NowPlayings"
import RecentGames from "./Component/RecentGames";
import "./index.scss";

function View(props) {
  return (
    <Layout>
      <div className="play">
        <SearchBarSection />
        {props.showsSearchResult ? (
          <SearchResultSection />
        ) : (
          <>
            <RecommendationSection />
            <WeeklyTopSection />
            <EditorPickSection />
            <NowPlayingSection />
            <RecentSection />
          </>
        )}
      </div>
    </Layout>
  );
}

export default injectIntl(View);

function SearchBarSection(props) {
  return (
    <div className="play__section_search">
      <SearchBar />
    </div>
  )
}

function SearchResultSection(props) {
  return (
    <div className="play__section">
      <SearchResult />
    </div>
  )
}

function RecommendationSection(props) {
  return (
    <Section
      title={<FormattedMessage id="ID_GAME_RECOMMEND_TITLE" />}
    >
      <Recommendations />
    </Section>
  )
}
function WeeklyTopSection(props) {
  return (
    <Section 
      title={<FormattedMessage id="ID_GAME_WEEKLY_LIKE_TITLE" />}
    >
      <WeeklyTops />
    </Section>
  )
}
function EditorPickSection(props) {
  return (
    <Section
      title={<FormattedMessage id="ID_GAME_EDITOR_TITLE" />}
      paddingNone={true}
      caption={
        <Link to="/editorRecommend">
          <FormattedMessage id="ID_GAME_EDITOR_MORE" />
        </Link>
      }
    >
      <EditorPicks />
    </Section>
  )
}
function NowPlayingSection(props) {
  return (
    <Section 
      title={<FormattedMessage id="ID_WIZAPP_VIEWS_TITLE" />}
    >
      <NowPlayings />
    </Section>
  )
}
function RecentSection(props) {
  return (
    <Section 
      title={<FormattedMessage id="ID_GAME_NEWS_GAME_TITLE" />}
    >
      <RecentGames />
    </Section>
  )
}

function Section(props) {
  return (
    <div className={`play__section ${props.paddingNone ? "section--padding-none" : ""}`}>
      <div className="play__section__header">
        <div className="play__section__title">
          {props.title || "-"}
        </div>
        {props.caption && (
          <div className="play__section__caption">
            {props.caption}
          </div>
        )}
      </div>
      <div className="play__section__body">
        {props.children}
      </div>
    </div>
  )
}
