import React from "react";
import Layout from "../../Common/Component/Layout";
import Quest from "./Components/Quest";
import Publish from "./Components/Publish";
import UserInfoTemplate from "./Components/UserInfoTemplate";
import "./index.scss";

export default function View(props) {
  // properties
  const {
    selectedTab,
    TAB_TYPE,
    profile,
    publisheds,
    email,
    match,
    intl
  } = props;
  const {
    handleSelectTab,
    handleProjectLive,
    onClickPublishedSetting,
    onClickSettingBtn,
    onClickMobileTooltip
  } = props;

  // logic
  let content;
  switch (selectedTab) {
    case TAB_TYPE.QUEST:
      content = <Quest />;
      break;
    case TAB_TYPE.PUBLISH:
      content = (
        <Publish
          intl={intl}
          match={match}
          publisheds={publisheds}
          onClickPublishedSetting={onClickPublishedSetting}
          handleProjectLive={handleProjectLive}
        />
      );
      break;
    default:
      break;
  }

  return (
    <Layout>
      <div className="Page--Mypage">
        <div className="Mypage--Inner">
          {profile && (
            <UserInfoTemplate
              intl={intl}
              match={match}
              userInfo={{ ...profile, email }}
              onClickSettingBtn={onClickSettingBtn}
              onClickMobileTooltip={onClickMobileTooltip}
            />
          )}

          <section className="Mypage__Activity">
            <div className="Activity_Tabs">
              {Object.keys(TAB_TYPE).map((tab, index) => (
                <div
                  key={index}
                  className={`Activity_Tabs-tap`}
                  style={
                    selectedTab === TAB_TYPE[tab] ? { color: "white" } : {}
                  }
                  onClick={() => {
                    handleSelectTab(TAB_TYPE[tab]);
                  }}
                >
                  {TAB_TYPE[tab]}
                </div>
              ))}
            </div>
            <div className="Activity_Tabs_Ani">
              <div
                className="Activity_Tabs_Ani-bar"
                style={
                  // selectedTab === TAB_TYPE.QUEST
                  //   ? { left: "calc(0%)" }
                  //   : selectedTab === TAB_TYPE.PUBLISH
                  //   ? { left: "calc(25%)" }
                  //   : selectedTab === TAB_TYPE.ACTIVITY
                  //   ? { left: "calc(50%)" }
                  //   : {}
                  selectedTab === TAB_TYPE.PUBLISH
                    ? { left: "calc(25%)" }
                    : {}
                }
              />
            </div>
            <div className="Activity_Contents">{content}</div>
          </section>
        </div>
      </div>
    </Layout>
  );
}
