import React from "react";
import { DndProvider } from "react-dnd-multi-backend";
import HTML5toTouch from "react-dnd-multi-backend/dist/esm/HTML5toTouch";
import "./index.scss";

export default function(props) {
  const { children, currentBlock, onClickContext } = props;

  return (
    <DndProvider options={HTML5toTouch}>
      <div
        id="oobceditor_context"
        className={`oobceditor_context${
          currentBlock ? " oobceditor_context-selectorOn" : ""
        }`}
        onClick={onClickContext}
      >
        <div className="oobceditor_lines">{children}</div>
      </div>
    </DndProvider>
  );
}
