import React from "react";
import "./index.scss";

const NameSetting = props => {
  const {
    intl,
    profile,
    handleNameSettingChange,
    handleSubmit,
    lastDateOfRename
  } = props;
  const pageData = [
    {
      title: "유저닉네임",
      type: "userName",
      // value: userName,
      placeHolder: profile.name
    },
    {
      title: "한줄 소개글",
      type: "userStatusMsg",
      // value: userStatusMsg,
      placeHolder: profile.statusMessage
        ? profile.statusMessage
        : intl.formatMessage({
            id: `ID_MYPAGE_USERINFO_STATUSMAG`
          })
    },
    {
      title: "소속",
      type: "userOrganization",
      // value: userOrganization,
      placeHolder: profile.organization
        ? profile.organization
        : intl.formatMessage({
            id: `ID_MYPAGE_USERINFO_ORGANIZATION`
          })
    }
  ];

  return (
    <div className="NameSetting">
      <div className="NameSetting_Inner">
        {pageData.map((data, index) => {
          if (data.type === "userName") {
            return (
              <div key={index} className="NameSetting_Wrap">
                <div
                  className="NameSetting_Wrap-title"
                  style={index < 2 ? null : { opacity: 1 }}
                >
                  {intl.formatMessage({
                    id: `ID_MYPAGE_NAME_${data.type.toUpperCase()}`
                  })}
                </div>
                <input
                  id={index === 0 ? "userName" : null}
                  className="NameSetting_Wrap-input name-input"
                  type="text"
                  name={data.type}
                  // value={data.value}
                  onChange={handleNameSettingChange}
                  placeholder={data.placeHolder}
                  disabled={
                    lastDateOfRename && lastDateOfRename < 15 ? true : false
                  }
                />
                {lastDateOfRename && lastDateOfRename < 15 && (
                  <div className="NameSetting-warn">
                    {intl.formatMessage({
                      id: `ID_MYPAGE_NAME_USERNAME_WARNING`
                    })}
                  </div>
                )}
              </div>
            );
          } else {
            return (
              <div key={index} className="NameSetting_Wrap">
                <div
                  className="NameSetting_Wrap-title"
                  style={index < 2 ? null : { opacity: 1 }}
                >
                  {intl.formatMessage({
                    id: `ID_MYPAGE_NAME_${data.type.toUpperCase()}`
                  })}
                </div>
                <input
                  id={index === 0 ? "userName" : null}
                  className="NameSetting_Wrap-input"
                  type="text"
                  name={data.type}
                  // value={data.value}
                  onChange={handleNameSettingChange}
                  placeholder={data.placeHolder}
                />
              </div>
            );
          }
        })}

        <div className="NameSetting-submit" onClick={handleSubmit}>
          {intl.formatMessage({
            id: `ID_MYPAGE_NAME_SUBMIT`
          })}
        </div>
      </div>
    </div>
  );
};

export default NameSetting;
