import React from 'react';
import { injectIntl, FormattedMessage } from "react-intl";
import { FIELD_TYPE } from "../../Util/Constant";
import Field from "../Field";
import { AGREEMENT } from "../../Util/Constant";
import "./index.scss";

import checkActive from "../../../Image/signup/check-active.svg";
import checkInactive from "../../../Image/signup/check-inactive.svg";

function View(props) {
  const { onClickLogin, isMobileSignupPage } = props;
  return (
    <div className="signup">
      <div className="signup__container">
        <div className="signup__header">
          {/* isMobileSignupPage <- G-STAR 용 회원가입 임시 페이지, G-STAR 종료 후 <MobileSignup/> 관련 코드 제거 필요 */}
          {isMobileSignupPage ? 
            <h1>레드브릭과 함께해요!</h1>
          :
            <>
            <h1><FormattedMessage id="ID_SIGNUP_TITLE"/></h1>
            <p><FormattedMessage id="ID_SIGNUP_DESC"/></p>
            </>
          }
        </div>
        <div className="signup__form">
          <IdField {...props} />
          <PwField {...props} />
          <BirthField {...props} />
        </div>
        <div className="signup__agrees">
          <AllAgreement {...props} />
          <ServiceTermsAgreement {...props} />
          <PrivacyAgreement {...props} />
          <MarketingAgreement {...props} />
          <AgreementWarning warning={props.warnings.agreement} />
        </div>
        <div className={`signup__button valid--${props.allValid}`}>
          <button onClick={props.onClickSignUp}>
            <FormattedMessage id="ID_SIGNUP_SIGNUP"/>
          </button>
          <p onClick={onClickLogin}>
            <FormattedMessage id="ID_SIGNUP_SIGNIN_DESC"/>{" "}
            <a><FormattedMessage id="ID_SIGNIN"/></a>
          </p>
        </div>
      </div>
    </div>
  )
}

const IdField = (props) => {
  const { intl, loginId, onChangeField, onBlurField, warnings, okSigns } = props;
  const information = { text: intl.formatMessage({ id: "ID_SIGNUP_ID_INFO"}) };
  return (
    <Field.Text
      id={FIELD_TYPE.LOGIN_ID}
      value={loginId}
      onChange={onChangeField}
      onBlur={onBlurField}
      warning={warnings.loginId}
      okSign={okSigns.loginId}
      title={intl.formatMessage({id:"ID_SIGNUP_ID"})}
      placeholder={intl.formatMessage({ id: `ID_SIGNUP_ID_PLACEHOLDER` })}
      informations={[information]}
    />
  );
};

const PwField = (props) => {
  const { intl, password, onChangeField, onBlurField, pwValidation, warnings } = props;
  const informations = [
    {
      text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_LENGTH"}),
      valid: pwValidation.length
    },
    {
      text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_MIX"}),
      valid: pwValidation.mix
    }
  ];
  return (
    <Field.SecureText
      id={FIELD_TYPE.PASSWORD}
      value={password}
      onChange={onChangeField}
      onBlur={onBlurField}
      warning={warnings.password}
      title={intl.formatMessage({id:"ID_SIGNUP_PW"})}
      placeholder={intl.formatMessage({ id: `ID_SIGNUP_PW_PLACEHOLDER` })}
      informations={informations}
      secureToggleOption={true}
    />
  );
};

const BirthField = (props) => {
  const { intl, birth, onChangeField, warnings } = props;
  const information = { text: intl.formatMessage({ id: "ID_SIGNUP_BIRTH_INFO"}) };
  return (
    <Field.Date
      id={FIELD_TYPE.BIRTH}
      value={birth}
      onChange={onChangeField}
      warning={warnings.birth}
      title={intl.formatMessage({id:"ID_SIGNUP_BIRTH"})}
      informations={[information]}
    />
  );
};

const AllAgreement = (props) => {
  return (
    <Agreement
      {...props}
      type={AGREEMENT.ALL}
      agreed={props.allAgreed}
    >
      <p>모두 동의합니다.</p>
    </Agreement>
  )
}

const ServiceTermsAgreement = (props) => {
  const type = AGREEMENT.SERVICE_TERMS;
  return (
    <>
      <Agreement
        {...props}
        type={type}
        agreed={props.agreed[type]}
      >
        <p>
          [필수] (주)레드브릭 통합 <span onClick={e=>props.onClickTerms(e, type)}>서비스 약관</span>에 동의합니다.
        </p>
      </Agreement>
      <p className="signup__agrees__agree__desc">
        <FormattedMessage id="ID_SIGNUP_TERMS" />
      </p>
    </>
  )
}

const PrivacyAgreement = (props) => {
  const type = AGREEMENT.PRIVACY;
  return (
    <Agreement
      {...props}
      type={type}
      agreed={props.agreed[type]}
    >
      <p>
        [필수] <span onClick={e=>props.onClickTerms(e, type)}>개인정보처리방침</span>에 동의합니다.
      </p>
    </Agreement>
  )
}

const MarketingAgreement = (props) => {
  const type = AGREEMENT.MARKETING;
  return (
    <Agreement
      {...props}
      type={type}
      agreed={props.agreed[type]}
    >
      <p>[선택] 서비스 정보 수신에 동의합니다.</p>
    </Agreement>
  )
}

const Agreement = ({ type, agreed, onClickAgree, children }) => {
 return (
    <div className={`signup__agrees__agree agree--${type} agreed--${agreed}`}>
      <div className={`signup__agrees__agree__title`} onClick={()=>onClickAgree(type)}>
        <img
          src={agreed ? checkActive : checkInactive}
          alt="check"
        />
        {children}
      </div>
    </div>
  );
};

const AgreementWarning = ({ warning }) => {
  return (
    <p className={`signup__agrees__warning visible--${!!warning}`}>
      {warning}
    </p>
  );
}

export default injectIntl(View);
