import React, { Component } from "react";
import "./index.scss";
import { injectIntl } from "react-intl";

class AccountConfirmPopup extends Component {
  onClickConfirm = () => {

  }

  render() {
    const { intl, email } = this.props

    return (
      <div className="account__manage__confirm-popup">
        <div className="account__manage__confirm-popup__title">
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_CONFIRM_POPUP_TITLE" })}
        </div>

        <div className="account__manage__confirm-popup__msg">
          <span>{email}</span>
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_CONFIRM_POPUP_MSG" })}
        </div>

        <button onClick={this.onClickConfirm}>
          {intl.formatMessage({ id: "ID_WIZLIVE_CLASS_MESSAGE_SUCCESS_OK" })}
        </button>
      </div>
    );
  }
}

export default injectIntl(AccountConfirmPopup);
