import React from "react";
import Layout from "../../../../Common/Component/Layout";
import "./index.scss";
import UserIconWrapper from "../../../../Common/Component/UserIconWrapper";
import officialIcon from "../../../../Image/certification-mark.svg";

export default function View(props) {
  const {
    intl,
    mode,
    MYPAGE_MODE,
    interestedUsers,
    subscribers,
    hoverInfo,
    profile,
    isMyPage
  } = props;
  const { handleSubscribe, handleUnSubscribe, onMouseHover } = props;
  const isSubsMode = mode === MYPAGE_MODE.SUBSCRIBE;
  return (
    <Layout>
      <div className="Page--Subscribe">
        <div className="Subscribe_Inner">
          <section className="Subscribe">
            <div className="Subscribe-title">
              {isSubsMode
                ? intl.formatMessage(
                    { id: "ID_MYPAGE_SUBSCRIBE_TITLE1" },
                    {
                      name: !isMyPage && profile ? profile.name : "회원",
                      count: subscribers ? subscribers.length : 0
                    }
                  )
                : intl.formatMessage(
                    { id: "ID_MYPAGE_SUBSCRIBE_TITLE2" },
                    {
                      name: !isMyPage && profile ? profile.name : "회원",
                      count: interestedUsers ? interestedUsers.length : 0
                    }
                  )}
            </div>

            <div className="Subscribe_ItemWrap">
              {isSubsMode
                ? subscribers &&
                  subscribers.map((userInfo, index) => (
                    <SubscribeItem
                      intl={intl}
                      key={index}
                      isSubsMode={isSubsMode}
                      userInfo={userInfo}
                      handleSubscribe={handleSubscribe}
                      handleUnSubscribe={handleUnSubscribe}
                      onMouseHover={onMouseHover}
                      hoverInfo={hoverInfo}
                      index={index}
                      style={
                        index === subscribers.length - 1
                          ? { borderBottom: "none" }
                          : {}
                      }
                    />
                  ))
                : interestedUsers &&
                  interestedUsers.map((userInfo, index) => (
                    <SubscribeItem
                      intl={intl}
                      key={index}
                      isSubsMode={isSubsMode}
                      userInfo={userInfo}
                      handleSubscribe={handleSubscribe}
                      handleUnSubscribe={handleUnSubscribe}
                      style={
                        index === interestedUsers.length - 1
                          ? { borderBottom: "none" }
                          : {}
                      }
                      onMouseHover={onMouseHover}
                      hoverInfo={hoverInfo}
                      index={index}
                    />
                  ))}
            </div>
          </section>
        </div>
      </div>
    </Layout>
  );
}

const SubscribeItem = props => {
  const {
    intl,
    userInfo,
    isSubsMode,
    handleSubscribe,
    handleUnSubscribe,
    hoverInfo,
    onMouseHover,
    index
  } = props;
  return (
    <div className="SubscribeItem" style={props.style}>
      <div className="SubscribeItem_Inner">
        <div className="SubscribeItem_Userinfo">
          <div style={{ width: "65px", height: "65px" }}>
            <UserIconWrapper iconSrc={userInfo.icon} />
          </div>
          <div className="SubscribeItem_Userinfo_TextWrap">
            <div className="SubscribeItem_Userinfo_TextWrap_FirstWrap">
              <div className="SubscribeItem_Userinfo_TextWrap_FirstWrap-name">
                {userInfo.name}
              </div>
              {userInfo.isOfficial && (
                <div className="SubscribeItem_Userinfo_TextWrap_FirstWrap-official">
                  <img
                    className="SubscribeItem_Userinfo_TextWrap_FirstWrap-official_img"
                    src={officialIcon}
                    alt={userInfo.name}
                  />
                </div>
              )}

              {/* <div className="SubscribeItem_Userinfo_TextWrap_FirstWrap-tier">
              </div> */}
            </div>
            <div className="SubscribeItem_Userinfo_TextWrap-des">
              {userInfo.statusMessage ? userInfo.statusMessage : ""}
            </div>

            <div className="SubscribeItem_Userinfo_TextWrap_LastWrap">
              <div className="SubscribeItem_Userinfo_TextWrap_LastWrap_Wrap">
                <div className="SubscribeItem_Userinfo_TextWrap_LastWrap_Wrap-text">
                  {intl.formatMessage(
                    { id: "ID_MYPAGE_SUBSCRIBE_USERINFO_GAME" },
                    {
                      count: userInfo.publishedProjectCount
                    }
                  )}
                </div>
                <div className="SubscribeItem_Userinfo_TextWrap_LastWrap_Wrap-divider" />
                <div className="SubscribeItem_Userinfo_TextWrap_LastWrap_Wrap-text">
                  {intl.formatMessage(
                    { id: "ID_MYPAGE_SUBSCRIBE_USERINFO_SUBSCRIBE" },
                    {
                      count: userInfo.subscribeCount
                    }
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* dynamic buttons */}
        {/* 구독자 상세 */}
        {isSubsMode ? (
          userInfo.isSubscribe ? (
            <div
              className={`SubscribeItem_Userinfo-${
                hoverInfo.onHover && hoverInfo.index === index
                  ? "btn"
                  : "ingBtn"
              }`}
              onMouseEnter={() => {
                onMouseHover(true, index);
              }}
              onMouseLeave={() => {
                onMouseHover(false, index);
              }}
              onClick={() => {
                handleUnSubscribe(userInfo.email);
              }}
            >
              {hoverInfo.onHover && hoverInfo.index === index
                ? intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_UNSUBSCRIBE" })
                : intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_SUBSCRIBING" })}
            </div>
          ) : (
            <div
              className="SubscribeItem_Userinfo-btn"
              onClick={() => handleSubscribe(userInfo.email)}
            >
              {intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_SUBSCRIBE" })}
            </div>
          )
        ) : // 관심회원 상세
        userInfo.isSubscribe ? (
          <div
            className={`SubscribeItem_Userinfo-${
              hoverInfo.onHover && hoverInfo.index === index ? "btn" : "ingBtn"
            }`}
            onMouseEnter={() => {
              onMouseHover(true, index);
            }}
            onMouseLeave={() => {
              onMouseHover(false, index);
            }}
            onClick={() => {
              handleUnSubscribe(userInfo.email);
            }}
          >
            {hoverInfo.onHover && hoverInfo.index === index
              ? intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_UNSUBSCRIBE" })
              : intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_SUBSCRIBING" })}
          </div>
        ) : (
          <div
            className="SubscribeItem_Userinfo-btn"
            onClick={() => handleSubscribe(userInfo.email)}
          >
            {intl.formatMessage({ id: "ID_MYPAGE_SUBSCRIBE_SUBSCRIBE" })}
          </div>
        )}
      </div>
    </div>
  );
};
