import { URL } from "./Constant";
import { detectIE } from "./detectBrowser";
import Achievement from './Achievement';
export var fetchRequest = function fetchRequest(url, method, param) {
  var headers = {
    "Content-Type": "application/json; charset=utf-8",
    platform: "wizlab"
  };

  if (detectIE()) {
    headers["Pragma"] = "no-cache";
  }

  if (localStorage.getItem("wizToken")) {
    headers["Authorization"] = localStorage.getItem("wizToken");
  }

  headers["locale"] = localStorage.getItem("wizLang");
  var requestInfo = {
    method: method,
    headers: headers
  };
  if (param) requestInfo["body"] = JSON.stringify(param);
  return new Promise(function (resolve, reject) {
    fetch(url, requestInfo).then(function (res) {
      Achievement.handleResponse(res);
      resolve(res);
    }).catch(function (error) {
      reject(error);
    });
  });
}; // videoclass temp

