import { URL } from "./Constant";
import { detectIE } from "./detectBrowser";
import Achievement from './Achievement'

export const fetchRequest = (url, method, param) => {
  var headers = {
    "Content-Type": "application/json; charset=utf-8",
    platform: "wizlab"
  };
  if (detectIE()) {
    headers["Pragma"] = "no-cache";
  }

  if (localStorage.getItem("wizToken")) {
    headers["Authorization"] = localStorage.getItem("wizToken");
  }

  headers["locale"] = localStorage.getItem("wizLang");

  const requestInfo = {
    method: method,
    headers: headers
  };

  if (param) requestInfo["body"] = JSON.stringify(param);

  return new Promise((resolve, reject) => {
    fetch(url, requestInfo)
      .then(res => {
        Achievement.handleResponse(res);
        resolve(res);
      })
      .catch(error => {
        reject(error);
      });
  });
};

