import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import moment from "moment";
import { Link } from "react-router-dom";
import closeIconWhite from "../../../Image/close-icon-white.svg";
import "./index.scss";

class EmailVerifyNotification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isClose: false
    };
  }

  get isAgeOver14() {
    const birth = this.props.userinfo.birth;
    const days = moment().diff(birth, "days")
    return Math.floor(days / 365) >= 14;
  }

  onClickClose = () => {
    this.setState({ isClose: true });
  };

  render() {
    const { isClose } = this.state;
    const { isAgeOver14, onClickClose } = this;

    if (isClose) {
      return null;
    }
    return (
      <div className="email-verify-notification">
        <Link className="email-verify-notification__link" to="/emailverify" >
          {isAgeOver14
            ? <FormattedMessage id="ID_EMAIL_VERIFY_MESSAGE_OVER_14" />
            : <FormattedMessage id="ID_EMAIL_VERIFY_MESSAGE_UNDER_14" />
          }
        </Link>
        <div className="email-verify-notification__button" onClick={onClickClose}>
          <img src={closeIconWhite} alt="close" />
        </div>
      </div>
    );
  }
}

export default EmailVerifyNotification;
