import React, { memo, useEffect, useState } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";
import { CircularProgressbarWithChildren } from "react-circular-progressbar";

import { Course, RecommendedProject, TodayQuest } from "../../../models";

import { getDashboard, getMyCourses, getRecommendProject } from "../api";

import Button from "../Components/Button";
import Title from "../Components/Title";

import { COLOR, renderTypeColor, DEFAULT_PROJECT_ID } from "./../Constants";
import { IMAGE, renderCircularIcon } from "./../Constants/Images";

const Self = styled.div`
  flex: 1;
  padding-bottom: 70px;

  @media screen and (max-width: 1169px) {
    padding-bottom: 20px;
  }
`;

const StyledLink = styled(Link)`
  text-decoration: none;
`;

const Row = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-left: -15px;
  margin-right: -15px;

  @media screen and (max-width: 1169px) {
    margin: 0;
  }
`;

const Col = styled.div`
  padding-left: 15px;
  padding-right: 15px;
  width: 100%;

  ${(props) => props.col === 2 && `width: 500px;`}
  ${(props) => props.col === 1 && `width: 400px;`}

  @media screen and (max-width: 1169px) {
    padding: 0;
    width: 88.33vw;
    margin: 0 auto;
  }
`;

const Widget = styled.div`
  padding: 20px;
  border-radius: 16px;
  background-color: #1c1c1c;
  margin-bottom: 30px;
  display: flex;
  flex-direction: column;

  ${(props) => props.height && `height: ${props.height}px;`}

  ${(props) => props.graph &&
    `
    padding: 0;
    flex-direction: row;
  `}

  ${(props) => props.recommend && `
    position: relative;
    padding: 10px;
    flex-direction: row;
  `}

  @media screen and (max-width: 1169px) {
    padding: 16px;

    ${(props) => props.recommend && `flex-direction: column;`}

    ${(props) => props.graph && `flex-direction: column;`}
  }
`;

const WidgetTitle = styled.div`
  font-size: 18px;
  font-weight: bold;
  line-height: 1.33;
  color: rgba(255, 255, 255, 0.5);
  margin-bottom: 20px;
`;

const WidgetBody = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const Greeting = styled.div`
  flex: 1;

  @media screen and (max-width: 1169px) {
    padding-bottom: 15.8px;
  }
`;

const TodayDreamPointMessage = styled.div`
  margin-bottom: 10px;
  font-size: 22px;
  line-height: 1.45;
  color: #ffffff;
`

const StrongBits = styled.strong``;

const TotalDreamPointWrap = styled.div`
  height: 36px;
  padding-right: 12px;
  border-radius: 22px;
  background-color: rgba(255, 255, 255, 0.1);
  display: inline-flex;
  justify-content: space-around;
  align-items: center;
`

const RankingIcon = styled.img`
  width: 36px;
  height: 36px;
`

const TotalDreamPoint = styled.span`
  margin-left: 4px;
  font-size: 18px;
  font-weight: 500;
  color: #ffffff;
`

const BIT = styled.span`
  margin-left: 4px;
  font-size: 12px;
  font-weight: bold;
  color: rgba(255, 255, 255, 0.5);
`

const GreetingIntro = styled.div`
  font-size: 16px;
  font-weight: bold;
  line-height: 1.5;
  color: #fff;
  margin-bottom: 10px;
`;

const GreetingMessage = styled.div`
  font-size: 22px;
  font-weight: bold;
  line-height: 1.45;
  color: #fff;
`;

const User = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  padding-top: 19.5px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);

  @media screen and (max-width: 1169px) {
    padding-top: 15.8px;
    padding-bottom: 38.5px;
  }
`;

const UserIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  background-image: url(${(props) => props.image});
`;

const UserInfo = styled.div`
  margin-left: 15px;
  flex: 1;

  @media screen and (max-width: 1169px) {
    margin-left: 19px;
  }
`;

const UserName = styled.div`
  font-size: 15px;
  font-weight: bold;
  line-height: 1;
  color: #fff;
  margin-bottom: 7px;

  @media screen and (max-width: 1169px) {
    max-width: calc(88.33vw - 32px - 60px - 19px);
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
`;

// const UserEmail = styled.div`
//   font-size: 13px;
//   font-weight: 500;
//   line-height: 1.15;
//   color: rgba(255, 255, 255, 0.5);

//   @media screen and (max-width: 1169px) {
//     max-width: calc(88.33vw - 32px - 60px - 19px);
//     overflow: hidden;
//     text-overflow: ellipsis;
//     white-space: nowrap;
//   }
// `;

const QuestsEmpty = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const QuestsEmptyTitle = styled.div`
  font-size: 16px;
  font-weight: bold;
  line-height: 1.5;
  color: #fff;
  margin-bottom: 5px;
`;

const QuestsEmptyDesc = styled.p`
  font-size: 16px;
  line-height: 1;
  letter-spacing: -0.1px;
  color: rgba(255, 255, 255, 0.5);
  margin-bottom: 30px;
`;

const Quests = styled.div`
  flex: 1;
  max-height: 186px;
  overflow: auto;

  @media screen and (max-width: 1169px) {
    max-height: unset;
    margin-bottom: 34px;
  }
`;

const QuestItem = styled.div`
  font-size: 16px;
  font-weight: 500;
  line-height: 1.5;
  color: #fff;
  display: flex;
  align-items: center;
  padding: 10.5px 0 12.5px;
  border-top: 1px solid rgba(255, 255, 255, 0.2);

  &:first-child {
    padding-top: 0;
    border-top: none;
  }
  &:last-child {
    padding-bottom: 0;
  }
`;

const Radio = styled.div`
  width: 20px;
  height: 20px;
