import React from "react";
import { injectIntl, FormattedMessage } from "react-intl"
import Layout from "../../Common/Component/Layout";
import { URL } from "../../Common/Util/Constant";
import Field from "./Field";
import "./index.scss";

import addIcon from "../../Image/challenge-icon-add.png";
import checkIcon from "../../Image/challenge-icon-check.svg";
import eventBanner from "../../Image/challenge-event-banner.png";
import completeBanner from "../../Image/challenge-complete-banner.png";

function View(props) {
  return (
    <Layout className="layout--challenge">
      {props.isCompleted ? (
        <ChallengeApplyComplete {...props} />
      ) : (
        <ChallengeApplyForm {...props} />
      )}
    </Layout>
  );
}

export default injectIntl(View);

function ChallengeApplyComplete(props) {
  return (
    <div className="challenge-apply challenge-apply--complete">
      <div className="challenge-apply--complete__title">
        {props.isEditMode
          ? props.intl.formatMessage({ id: "ID_CHALLENGE_APPLY_COMPLETE_UPDATE_TITLE" })
          : props.intl.formatMessage({ id: "ID_CHALLENGE_APPLY_COMPLETE_SUBMIT_TITLE" })
        }
      </div>
      <a
        className="challenge-apply--complete__banner"
        href="https://wizlive.com/curriculum/detail_python"
        target="_blank"
        rel="noopener noreferrer"
      >
        <img
          className="challenge-apply--complete__banner__backgroundImg"
          src={completeBanner}
          alt="complete-banner"
        />
        <video
          className="challenge-apply--complete__banner__video"
          src={URL.S3_ETC + "/creator-challenge-wizlive-commercial.mp4"}
          autoPlay
          loop
        />
      </a>
      <button
        className="challenge-apply__button"
        onClick={props.onClickComplete}
      >
        {props.intl.formatMessage({ id: "ID_CHALLENGE_APPLY_COMPLETE" })}
      </button>
