import { GRAMMAR, BLOCK, DATA } from "./Type";

export default [
  {
    name: "repeat",
    dataType: DATA.UTIL,
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.NUMBER],
          [BLOCK.VARIABLE]: [DATA.NUMBER],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT, DATA.SCREEN, DATA.JOYSTICK]
        }
      },
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "if",
    dataType: DATA.UTIL,
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.BOOLEAN],
          [BLOCK.VARIABLE]: [DATA.BOOLEAN],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      },
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "else if",
    dataType: DATA.UTIL,
    childs: [
      {
        grammars: {
          [BLOCK.CONSTANT]: GRAMMAR.OBJECTIVE,
          [BLOCK.VARIABLE]: GRAMMAR.OBJECTIVE,
          [BLOCK.SPRITE]: GRAMMAR.OBJECTIVE
        },
        blockTypes: [BLOCK.CONSTANT, BLOCK.VARIABLE, BLOCK.SPRITE],
        dataTypes: {
          [BLOCK.CONSTANT]: [DATA.BOOLEAN],
          [BLOCK.VARIABLE]: [DATA.BOOLEAN],
          [BLOCK.SPRITE]: [DATA.SPRITE, DATA.TEXT]
        }
      },
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "else",
    dataType: DATA.UTIL,
    childs: [
      {
        blockTypes: [BLOCK.CALLBACK]
      }
    ]
  },
  {
    name: "clone",
    parentDataTypes: [DATA.SPRITE],
    dataType: DATA.SPRITE
  },
  {
    name: "mobile",
    dataType: DATA.MOBILE,
    childs: [
      {
        grammars: {
          [BLOCK.ACTION]: GRAMMAR.VERB
        },
        blockTypes: [BLOCK.ACTION],
        dataTypes: {
          [BLOCK.ACTION]: [DATA.MOBILE]
        }
      }
    ]
  }
];
