import React from "react";
import { injectIntl } from "react-intl";
import "./index.scss";

import searchIcon from "../../../../Image/renewal_icon_search.svg";

function View(props) {
  return (
    <div className="play__search-bar">
      <input
        className="play__search-bar__input"
        placeholder={props.intl.formatMessage({ id: "ID_SEARCH_HINT" })}
        value={props.searchText}
        onChange={props.onChangeSearchText}
        onKeyDown={e => {
          if(e.keyCode === 13) {
            props.onClickSearch();
          }
        }}
      />
      <button
        className="play__search-bar__button"
        onClick={props.onClickSearch}
      >
        <img
          className="play__search-bar__icon"
          src={searchIcon}
          alt="search"
        />
      </button>
    </div>
  )
}

export default injectIntl(View);
